def create_row_hv(row, dim, random_seeds):

    def generate_hv(seed, dim):
        """Generate a random hypervector with a given seed."""
        np.random.seed(seed)
        return np.random.choice([-1, 1], size=dim)

    def bind_hv(hv1, hv2):
        """Bind two hypervectors using element-wise multiplication."""
        return hv1 * hv2
    composite_hv = np.ones(dim)
    for (feature, value) in row.items():
        feature_seed = random_seeds.get(feature, None)
        if feature_seed is None:
            raise ValueError(f"Seed for feature '{feature}' not provided in random_seeds.")
        feature_hv = generate_hv(feature_seed, dim)
        value_seed = hash((feature_seed, value))
        value_hv = generate_hv(value_seed, dim)
        feature_value_hv = bind_hv(feature_hv, value_hv)
        composite_hv = bind_hv(composite_hv, feature_value_hv)
    return composite_hv.tolist()

