def performance_metrics(actual: List[int], predicted: List[int]) -> Tuple[List[List[int]], float, float, float, float]:
    if len(actual) != len(predicted):
        raise ValueError("The 'actual' and 'predicted' lists must have the same length.")
    if not all((x in [0, 1] for x in actual + predicted)):
        raise ValueError("All elements in 'actual' and 'predicted' lists must be either 0 or 1.")
    true_positives = 0
    true_negatives = 0
    false_positives = 0
    false_negatives = 0
    for (a, p) in zip(actual, predicted):
        if a == 1 and p == 1:
            true_positives += 1
        elif a == 0 and p == 0:
            true_negatives += 1
        elif a == 0 and p == 1:
            false_positives += 1
        elif a == 1 and p == 0:
            false_negatives += 1
    confusion_matrix = [[true_negatives, false_positives], [false_negatives, true_positives]]
    accuracy = (true_positives + true_negatives) / (true_positives + true_negatives + false_positives + false_negatives)
    if true_positives + false_positives == 0 or true_positives + false_negatives == 0:
        f1_score = 0.0
    else:
        precision = true_positives / (true_positives + false_positives)
        recall = true_positives / (true_positives + false_negatives)
        f1_score = 2 * (precision * recall) / (precision + recall)
    if true_negatives + false_positives == 0:
        specificity = 0.0
    else:
        specificity = true_negatives / (true_negatives + false_positives)
    if true_negatives + false_negatives == 0:
        negative_predictive_value = 0.0
    else:
        negative_predictive_value = true_negatives / (true_negatives + false_negatives)
    return (confusion_matrix, round(accuracy, 3), round(f1_score, 3), round(specificity, 3), round(negative_predictive_value, 3))

