from collections import Counter
def meteor_score(reference, candidate, alpha=0.9, beta=3, gamma=0.5):

    def _tokenize(text):
        return text.split()

    def _get_chunks(sequence, n):
        return [sequence[i:i + n] for i in range(len(sequence) - n + 1)]

    def _get_ngram_counts(sequence, n):
        return Counter(_get_chunks(sequence, n))

    def _get_exact_match_counts(ref_chunks, cand_chunks):
        return sum((min(ref_chunks[chunk], cand_chunks[chunk]) for chunk in ref_chunks))

    def _get_chrf_counts(ref_chunks, cand_chunks):
        insertion = sum((cand_chunks[chunk] - ref_chunks[chunk] for chunk in cand_chunks if cand_chunks[chunk] > ref_chunks[chunk]))
        deletion = sum((ref_chunks[chunk] - cand_chunks[chunk] for chunk in ref_chunks if ref_chunks[chunk] > cand_chunks[chunk]))
        return (insertion, deletion)
    ref_tokens = _tokenize(reference)
    cand_tokens = _tokenize(candidate)
    ref_unigrams = _get_ngram_counts(ref_tokens, 1)
    cand_unigrams = _get_ngram_counts(cand_tokens, 1)
    exact_matches = _get_exact_match_counts(ref_unigrams, cand_unigrams)
    precision = exact_matches / len(cand_tokens) if cand_tokens else 0
    recall = exact_matches / len(ref_tokens) if ref_tokens else 0
    if precision + recall == 0:
        f_mean = 0
    else:
        f_mean = (1 + beta ** 2) * (precision * recall) / (beta ** 2 * precision + recall)
    (chrf1_ins, chrf1_del) = _get_chrf_counts(ref_unigrams, cand_unigrams)
    (chrf2_ins, chrf2_del) = _get_chrf_counts(_get_ngram_counts(ref_tokens, 2), _get_ngram_counts(cand_tokens, 2))
    fragmentation = (chrf1_ins + chrf1_del) / (2 * len(ref_tokens)) + gamma * (chrf2_ins + chrf2_del) / (2 * len(ref_tokens))
    penalty = 1 - np.exp(-fragmentation)
    meteor = (1 - alpha) * f_mean - alpha * penalty
    return round(meteor, 3)
