def min_max(x: list[int]) -> list[float]:
    if not x:
        return []
    min_val = min(x)
    max_val = max(x)
    if min_val == max_val:
        return [0.0 for _ in x]
    normalized = [(val - min_val) / (max_val - min_val) for val in x]
    return [round(val, 4) for val in normalized]

import numpy as np
def batch_normalization(X: np.ndarray, gamma: np.ndarray, beta: np.ndarray, epsilon: float=1e-05):
    (batch_size, channels, height, width) = X.shape
    X_reshaped = X.reshape(batch_size, channels, -1)
    mean = np.mean(X_reshaped, axis=(0, 2), keepdims=True)
    var = np.var(X_reshaped, axis=(0, 2), keepdims=True)
    X_normalized = (X_reshaped - mean) / np.sqrt(var + epsilon)
    X_normalized = X_normalized.reshape(batch_size, channels, height, width)
    X_normalized = gamma[:, np.newaxis, np.newaxis] * X_normalized + beta[:, np.newaxis, np.newaxis]
    return X_normalized.round(4).tolist()

