def rref(matrix):
    A = np.array(matrix, dtype=float)
    (rows, cols) = A.shape
    lead = 0
    for r in range(rows):
        if cols <= lead:
            return A.tolist()
        i = r
        while A[i, lead] == 0:
            i += 1
            if rows == i:
                i = r
                lead += 1
                if cols == lead:
                    return A.tolist()
        A[[i, r]] = A[[r, i]]
        lv = A[r, lead]
        A[r] /= lv
        for i in range(rows):
            if i != r:
                rv = A[i, lead]
                A[i] -= rv * A[r]
        lead += 1
    return A.tolist()
