def solve_jacobi(A: np.ndarray, b: np.ndarray, n: int) -> list:
    N = len(b)
    x = np.zeros(N)
    for _ in range(n):
        x_new = np.zeros(N)
        for i in range(N):
            s = sum((A[i, j] * x[j] for j in range(N) if i != j))
