from collections import Counter
def learn_decision_tree(examples: list[dict], attributes: list[str], target_attr: str) -> dict:

    def entropy(class_counts: Counter) -> float:
        total = sum(class_counts.values())
        return -sum((count / total * math.log2(count / total) for count in class_counts.values() if count > 0))

    def information_gain(examples: list[dict], attribute: str, target_attr: str) -> float:
        class_counts = Counter((example[target_attr] for example in examples))
        total_entropy = entropy(class_counts)
        value_counts = Counter((example[attribute] for example in examples))
        weighted_entropy = 0.0
        for value in value_counts:
            subset = [example for example in examples if example[attribute] == value]
            subset_entropy = entropy(Counter((example[target_attr] for example in subset)))
            weighted_entropy += len(subset) / len(examples) * subset_entropy
        return total_entropy - weighted_entropy

    def choose_best_attribute(examples: list[dict], attributes: list[str], target_attr: str) -> str:
        return max(attributes, key=lambda attr: information_gain(examples, attr, target_attr))

    def plurality_value(examples: list[dict], target_attr: str) -> str:
        return Counter((example[target_attr] for example in examples)).most_common(1)[0][0]

    def all_same_classification(examples: list[dict], target_attr: str) -> bool:
        return len(set((example[target_attr] for example in examples))) == 1
    if not examples:
        return {}
    if all_same_classification(examples, target_attr):
        return examples[0][target_attr]
    if not attributes:
        return plurality_value(examples, target_attr)
    best_attribute = choose_best_attribute(examples, attributes, target_attr)
    tree = {best_attribute: {}}
    for value in set((example[best_attribute] for example in examples)):
        subset = [example for example in examples if example[best_attribute] == value]
        remaining_attributes = [attr for attr in attributes if attr != best_attribute]
        subtree = learn_decision_tree(subset, remaining_attributes, target_attr)
        tree[best_attribute][value] = subtree
    return tree

