def adaboost_fit(X, y, n_clf):
    (n_samples, n_features) = X.shape
    sample_weights = np.full(n_samples, 1 / n_samples)
    classifiers = []

    def weighted_error(error, sample_weights):
        return round(np.sum(error * sample_weights), 4)

    def calculate_alpha(error):
        return round(0.5 * math.log((1 - error) / error), 4)

    def update_weights(alpha, predictions, sample_weights):
        sample_weights *= np.exp(-alpha * y * predictions)
        sample_weights /= np.sum(sample_weights)
        return sample_weights.round(4)
    for _ in range(n_clf):
        best_clf_error = float('inf')
        best_feature = None
        best_threshold = None
        best_polarity = None
        for feature_i in range(n_features):
            feature_values = X[:, feature_i]
            thresholds = np.unique(feature_values)
            for threshold in thresholds:
                for polarity in [1, -1]:
                    predictions = np.ones(n_samples)
                    predictions[feature_values * polarity < threshold * polarity] = -1
                    error = np.zeros(n_samples)
                    error[predictions != y] = 1
                    weighted_err = weighted_error(error, sample_weights)
                    if weighted_err < best_clf_error:
                        best_clf_error = weighted_err
                        best_feature = feature_i
                        best_threshold = threshold
                        best_polarity = polarity
        alpha = calculate_alpha(best_clf_error)
        sample_weights = update_weights(alpha, best_polarity * (X[:, best_feature] < best_threshold), sample_weights)
        classifiers.append({'feature_index': best_feature, 'threshold': round(best_threshold, 4), 'alpha': alpha, 'polarity': best_polarity})
    return classifiers

