def dice_score(y_true, y_pred):
    y_true = np.array(y_true)
    y_pred = np.array(y_pred)
    intersection = np.sum(y_true * y_pred)
    sum_true_pred = np.sum(y_true) + np.sum(y_pred)
    if sum_true_pred == 0:
        return 1.0
    dice = 2 * intersection / sum_true_pred
    return round(dice, 3)

