from typing import List, Tuple
def performance_metrics(actual: List[int], predicted: List[int]) -> Tuple[List[List[int]], float, float, float, float]:
    if len(actual) != len(predicted):
        raise ValueError('The length of actual and predicted lists must be the same.')
    if not all((x in [0, 1] for x in actual + predicted)):
        raise ValueError('All elements in actual and predicted lists must be either 0 or 1.')
    true_positives = 0
    true_negatives = 0
    false_positives = 0
    false_negatives = 0
    for (a, p) in zip(actual, predicted):
        if a == 1 and p == 1:
            true_positives += 1
        elif a == 0 and p == 0:
            true_negatives += 1
        elif a == 0 and p == 1:
            false_positives += 1
        elif a == 1 and p == 0:
            false_negatives += 1
    confusion_matrix = [[true_negatives, false_positives], [false_negatives, true_positives]]
    total = true_positives + true_negatives + false_positives + false_negatives
    accuracy = round((true_positives + true_negatives) / total, 3) if total != 0 else 0.0
    precision = round(true_positives / (true_positives + false_positives), 3) if true_positives + false_positives != 0 else 0.0
    recall = round(true_positives / (true_positives + false_negatives), 3) if true_positives + false_negatives != 0 else 0.0
    f1_score = round(2 * (precision * recall) / (precision + recall), 3) if precision + recall != 0 else 0.0
    specificity = round(true_negatives / (true_negatives + false_positives), 3) if true_negatives + false_positives != 0 else 0.0
    negative_predictive_value = round(true_negatives / (true_negatives + false_negatives), 3) if true_negatives + false_negatives != 0 else 0.0
    return (confusion_matrix, accuracy, f1_score, specificity, negative_predictive_value)

