        e_values = np.exp(values - np.max(values))
        return e_values / np.sum(e_values)
    crystal_values = np.array(crystal_values)
    attention_scores = np.zeros((n, n))
    for i in range(n):
        for j in range(n):
            attention_scores[i, j] = np.dot(crystal_values[i], crystal_values[j])
    attention_weights = np.apply_along_axis(softmax, 1, attention_scores)
    weighted_patterns = np.dot(attention_weights, crystal_values)
