from collections import Counter
def meteor_score(reference, candidate, alpha=0.9, beta=3, gamma=0.5):
    ref_tokens = reference.split()
    cand_tokens = candidate.split()
    ref_counter = Counter(ref_tokens)
    cand_counter = Counter(cand_tokens)
    matches = sum((ref_counter & cand_counter).values())
    ref_len = len(ref_tokens)
    cand_len = len(cand_tokens)
    precision = matches / cand_len if cand_len > 0 else 0
    recall = matches / ref_len if ref_len > 0 else 0
    if precision + recall > 0:
        f_mean = (1 + beta ** 2) * (precision * recall) / (beta ** 2 * precision + recall)
    else:
        f_mean = 0
    alignment_matrix = np.zeros((ref_len + 1, cand_len + 1))
    for i in range(1, ref_len + 1):
        for j in range(1, cand_len + 1):
            if ref_tokens[i - 1] == cand_tokens[j - 1]:
                alignment_matrix[i][j] = alignment_matrix[i - 1][j - 1] + 1
            else:
                alignment_matrix[i][j] = max(alignment_matrix[i - 1][j], alignment_matrix[i][j - 1])
    aligned_words = []
    (i, j) = (ref_len, cand_len)
    while i > 0 and j > 0:
        if ref_tokens[i - 1] == cand_tokens[j - 1]:
            aligned_words.append(ref_tokens[i - 1])
            i -= 1
            j -= 1
        elif alignment_matrix[i - 1][j] > alignment_matrix[i][j - 1]:
            i -= 1
        else:
            j -= 1
    if len(aligned_words) > 0:
        breaks = 0
        start = 0
        for word in aligned_words:
            try:
                start = ref_tokens.index(word, start) + 1
            except ValueError:
                break
            else:
                breaks += 1
        fragmentation = (len(aligned_words) - breaks) / len(aligned_words)
    else:
        fragmentation = 1
    frag_penalty = (1 - gamma) * fragmentation
    meteor = (1 - alpha) * f_mean - alpha * frag_penalty
    return round(meteor, 3)

def min_max(x: list[int]) -> list[float]:
    if not x:
        return []
    min_val = min(x)
    max_val = max(x)
    if min_val == max_val:
        return [0.0] * len(x)
    normalized = [(val - min_val) / (max_val - min_val) for val in x]
    return [round(val, 4) for val in normalized]

import numpy as np
def batch_normalization(X: np.ndarray, gamma: np.ndarray, beta: np.ndarray, epsilon: float=1e-05):
    (batch, channels, height, width) = X.shape
    X_reshaped = X.reshape(batch, channels, -1)
    mean = np.mean(X_reshaped, axis=(0, 2), keepdims=True)
    var = np.var(X_reshaped, axis=(0, 2), keepdims=True)
    X_normalized = (X_reshaped - mean) / np.sqrt(var + epsilon)
    X_normalized = X_normalized.reshape(batch, channels, height, width)
    Y = gamma.reshape(1, -1, 1, 1) * X_normalized + beta.reshape(1, -1, 1, 1)
    return Y.round(4).tolist()

