import numpy as np
def bhattacharyya_distance(p: list[float], q: list[float]) -> float:
    if len(p) != len(q) or not p:
        return 0.0
    p = np.array(p)
    q = np.array(q)
    bc = np.sum(np.sqrt(p * q))
    bd = -np.log(bc) if bc > 0 else float('inf')
    return round(bd, 4)

