import numpy as np
def compute_policy_gradient(theta: np.ndarray, episodes: list[list[tuple[int, int, float]]]):
    (num_states, num_actions) = theta.shape
    gradient = np.zeros_like(theta, dtype=float)
    for episode in episodes:
        G = 0
        returns = []
        for t in reversed(range(len(episode))):
            (s, a, r) = episode[t]
            G += r
            returns.append(G)
        returns.reverse()
        for (t, (s, a, r)) in enumerate(episode):
            G = returns[t]
            probs = np.exp(theta[s, :]) / np.sum(np.exp(theta[s, :]))
            gradient[s, a] += (1 - probs[a]) * G
            for a_ in range(num_actions):
                if a_ != a:
                    gradient[s, a_] -= probs[a_] * G
    gradient /= len(episodes)
    gradient = np.round(gradient, 4)
    return gradient.tolist()

import numpy as np
def find_treasure(start_x: float, learning_rate: float=0.1, tolerance: float=1e-06, max_iters: int=10000) -> float:

    def f(x):
        return x ** 4 - 3 * x ** 3 + 2

    def df(x):
        return 4 * x ** 3 - 9 * x ** 2
    x = start_x
    for _ in range(max_iters):
        gradient = df(x)
        new_x = x - learning_rate * gradient
        if abs(new_x - x) < tolerance:
            break
        x = new_x
    return round(x, 4)
