def solve_jacobi(A: np.ndarray, b: np.ndarray, n: int) -> list:
    if A.shape[0] != A.shape[1]:
        raise ValueError('Matrix A must be square.')
    if A.shape[0] != b.shape[0]:
        raise ValueError('Matrix A and vector b must have the same number of rows.')
    x = np.zeros_like(b, dtype=float)
    N = A.shape[0]
    for _ in range(n):
        x_new = np.zeros_like(x, dtype=float)
        for i in range(N):
            s = sum((A[i, j] * x[j] for j in range(N) if j != i))
            x_new[i] = (b[i] - s) / A[i, i]
        x_new = np.round(x_new, 4)
        x = x_new
    return x.tolist()

def determinant_4x4(matrix: list[list[int | float]]) -> float:

    def determinant_3x3(minor: list[list[int | float]]) -> float:
        (a, b, c) = minor[0]
        (d, e, f) = minor[1]
        (g, h, i) = minor[2]
        return a * (e * i - f * h) - b * (d * i - f * g) + c * (d * h - e * g)
    if len(matrix) != 4 or any((len(row) != 4 for row in matrix)):
        raise ValueError('Input must be a 4x4 matrix.')
    det = 0
    for c in range(4):
        minor = [[matrix[r][c2] for c2 in range(4) if c2 != c] for r in range(1, 4)]
        det += (-1) ** c * matrix[0][c] * determinant_3x3(minor)
    return det
