def pegasos_kernel_svm(data: np.ndarray, labels: np.ndarray, kernel='linear', lambda_val=0.01, iterations=100, sigma=1.0):

    def linear_kernel(x1, x2):
        return np.dot(x1, x2)

    def rbf_kernel(x1, x2, sigma=1.0):
        return np.exp(-np.linalg.norm(x1 - x2) ** 2 / (2 * sigma ** 2))

    def kernel_function(x1, x2):
        if kernel == 'linear':
            return linear_kernel(x1, x2)
        elif kernel == 'rbf':
            return rbf_kernel(x1, x2, sigma)
        else:
            raise ValueError("Unsupported kernel type. Use 'linear' or 'rbf'.")
    (n_samples, n_features) = data.shape
    alphas = np.zeros(n_samples)
    bias = 0.0
    for t in range(1, iterations + 1):
        learning_rate = 1.0 / (lambda_val * t)
        for i in range(n_samples):
            xi = data[i]
            yi = labels[i]
            prediction = sum((alphas[j] * yi * kernel_function(data[j], xi) for j in range(n_samples))) + bias
            hinge_loss = max(0, 1 - yi * prediction)
            if hinge_loss > 0:
                alphas[i] += learning_rate * yi
                bias += learning_rate * yi
        alphas = np.clip(alphas, 0, 1.0 / (lambda_val * n_samples))
    alphas = np.round(alphas, 4).tolist()
    bias = np.round(bias, 4)
    return (alphas, bias)

