def rref(matrix):
    mat = np.array(matrix, dtype=float)
    (rows, cols) = mat.shape
    row = 0
    for col in range(cols):
        if row >= rows:
            break
        if mat[row, col] == 0:
            for r in range(row + 1, rows):
                if mat[r, col] != 0:
                    mat[[row, r]] = mat[[r, row]]
                    break
            else:
                continue
        pivot = mat[row, col]
        mat[row] /= pivot
        for r in range(rows):
            if r != row:
                factor = mat[r, col]
                mat[r] -= factor * mat[row]
        row += 1
    return mat.tolist()

