def create_row_hv(row, dim, random_seeds):

    def generate_random_hv(seed, dim):
        np.random.seed(seed)
        return np.random.choice([-1, 1], size=dim)
    composite_hv = np.zeros(dim)
    for (feature, value) in row.items():
        feature_seed = random_seeds.get(feature, None)
        if feature_seed is None:
            raise ValueError(f"Seed for feature '{feature}' not provided in random_seeds.")
        feature_hv = generate_random_hv(feature_seed, dim)
        value_seed = hash(str(value)) + feature_seed
        value_hv = generate_random_hv(value_seed, dim)
        bound_hv = feature_hv * value_hv
        composite_hv += bound_hv
    composite_hv = np.sign(composite_hv)
    return composite_hv.tolist()

