def performance_metrics(actual: List[int], predicted: List[int]) -> Tuple[List[List[int]], float, float, float, float]:
    if len(actual) != len(predicted):
        raise ValueError("The 'actual' and 'predicted' lists must have the same length.")
    if not all((x in [0, 1] for x in actual + predicted)):
        raise ValueError("All elements in 'actual' and 'predicted' lists must be either 0 or 1.")
    true_positives = 0
    true_negatives = 0
    false_positives = 0
    false_negatives = 0
    for (a, p) in zip(actual, predicted):
        if a == 1 and p == 1:
            true_positives += 1
        elif a == 0 and p == 0:
            true_negatives += 1
        elif a == 0 and p == 1:
            false_positives += 1
        elif a == 1 and p == 0:
            false_negatives += 1
    confusion_matrix = [[true_negatives, false_positives], [false_negatives, true_positives]]
    total = true_positives + true_negatives + false_positives + false_negatives
    accuracy = round((true_positives + true_negatives) / total, 3) if total != 0 else 0.0
    precision = round(true_positives / (true_positives + false_positives), 3) if true_positives + false_positives != 0 else 0.0
    recall = round(true_positives / (true_positives + false_negatives), 3) if true_positives + false_negatives != 0 else 0.0
    f1_score = round(2 * (precision * recall) / (precision + recall), 3) if precision + recall != 0 else 0.0
    specificity = round(true_negatives / (true_negatives + false_positives), 3) if true_negatives + false_positives != 0 else 0.0
    negative_predictive_value = round(true_negatives / (true_negatives + false_negatives), 3) if true_negatives + false_negatives != 0 else 0.0
    return (confusion_matrix, accuracy, f1_score, specificity, negative_predictive_value)

import numpy as np
def pos_encoding(position: int, d_model: int):
    if position == 0 or d_model <= 0:
        return -1
    pe = np.zeros((position, d_model), dtype=np.float16)
    for pos in range(position):
        for i in range(0, d_model, 2):
            if i < d_model:
                pe[pos, i] = np.sin(pos / 10000 ** (2 * i / d_model))
            if i + 1 < d_model:
                pe[pos, i + 1] = np.cos(pos / 10000 ** (2 * (i + 1) / d_model))
    return pe.tolist()

def model_fit_quality(training_accuracy, test_accuracy):
    """
    Determine if the model is overfitting, underfitting, or a good fit based on training and test accuracy.
    :param training_accuracy: float, training accuracy of the model (0 <= training_accuracy <= 1)
    :param test_accuracy: float, test accuracy of the model (0 <= test_accuracy <= 1)
    :return: int, one of '1', '-1', or '0'.
    """
    if training_accuracy - test_accuracy > 0.2:
        return 1
    elif training_accuracy < 0.7 and test_accuracy < 0.7:
        return -1
    else:
        return 0

import numpy as np
def pattern_weaver(n, crystal_values, dimension):

    def softmax(values):
        exp_values = np.exp(values - np.max(values))
        return exp_values / np.sum(exp_values)
    crystal_values = np.array(crystal_values)
    attention_scores = np.zeros((n, n))
    for i in range(n):
        for j in range(n):
            attention_scores[i, j] = np.dot(crystal_values[i], crystal_values[j])
    attention_weights = np.apply_along_axis(softmax, 1, attention_scores)
    weighted_patterns = np.dot(attention_weights, crystal_values)
    return np.round(weighted_patterns, 4).tolist()
