def selu(x: float) -> float:
    """
    Implements the SELU (Scaled Exponential Linear Unit) activation function.

    Args:
        x: Input value

    Returns:
        SELU activation value
    """
    alpha = 1.6732632423543772
    scale = 1.0507009873554805
    if x > 0:
        return round(scale * x, 4)
    else:
        return round(scale * alpha * (math.exp(x) - 1), 4)

import numpy as np
def train_logreg(X: np.ndarray, y: np.ndarray, learning_rate: float, iterations: int) -> tuple[list[float], list[float]]:
    """
    Gradient-descent training algorithm for logistic regression, optimizing parameters with Binary Cross Entropy loss.
    """
    X = np.c_[np.ones((X.shape[0], 1)), X]
    weights = np.zeros(X.shape[1])
    loss_values = []

    def sigmoid(z):
        return 1 / (1 + np.exp(-z))

    def compute_loss(y, y_pred):
        return -np.mean(y * np.log(y_pred) + (1 - y) * np.log(1 - y_pred))
    for i in range(iterations):
        z = np.dot(X, weights)
        y_pred = sigmoid(z)
        gradient = np.dot(X.T, y_pred - y) / y.size
        weights -= learning_rate * gradient
        loss = compute_loss(y, y_pred)
        loss_values.append(round(loss, 4))
    return (weights.tolist(), loss_values)

