from collections import Counter
from math import exp
def meteor_score(reference, candidate, alpha=0.9, beta=3, gamma=0.5):

    def _match(ngram, cands):
        """Given a ngram, return the number of candidates it matches with."""
        for cand in cands:
            if ngram in cand:
                return 1
        return 0

    def _ngram_counts(sentence, n):
        """Return a Counter object of n-grams in the sentence."""
        return Counter([' '.join(sentence[i:i + n]) for i in range(len(sentence) - n + 1)])

    def _word_order(candidate, reference):
        """Calculate the word order penalty."""
        candidate_chunks = []
        ref_chunks = []
        candidate_words = candidate.split()
        ref_words = reference.split()
        for i in range(len(candidate_words)):
            for j in range(i, len(candidate_words)):
                chunk = ' '.join(candidate_words[i:j + 1])
                if chunk in reference:
                    candidate_chunks.append(chunk)
        for i in range(len(ref_words)):
            for j in range(i, len(ref_words)):
                chunk = ' '.join(ref_words[i:j + 1])
                if chunk in candidate:
                    ref_chunks.append(chunk)
        if not candidate_chunks or not ref_chunks:
            return 0
        candidate_chunks = sorted(candidate_chunks, key=len, reverse=True)
        ref_chunks = sorted(ref_chunks, key=len, reverse=True)
        matches = 0
        for chunk in candidate_chunks:
            if chunk in ref_chunks:
                matches += 1
                ref_chunks.remove(chunk)
        return matches / len(ref_chunks)
    ref_words = reference.split()
    cand_words = candidate.split()
    ref_counts = Counter(ref_words)
    cand_counts = Counter(cand_words)
    overlap = sum((ref_counts & cand_counts).values())
    precision = overlap / len(cand_words) if cand_words else 0
    recall = overlap / len(ref_words) if ref_words else 0
    if precision + recall > 0:
        f_mean = (1 + beta ** 2) * (precision * recall) / (beta ** 2 * precision + recall)
    else:
        f_mean = 0
    word_order_penalty = _word_order(candidate, reference)
    if word_order_penalty == 0:
        word_order_penalty = exp(-gamma * abs(len(cand_words) - len(ref_words)))
    meteor = (1 - alpha) * f_mean + alpha * word_order_penalty
    return round(meteor, 3)
