import numpy as np
def bhattacharyya_distance(p: list[float], q: list[float]) -> float:
    if len(p) != len(q) or not p or (not q):
        return 0.0
    p = np.array(p)
    q = np.array(q)
    bc = np.sum(np.sqrt(p * q))
    bd = -np.log(bc) if bc > 0 else float('inf')
    return round(bd, 4)

def vector_sum(a: list[int | float], b: list[int | float]) -> list[int | float] | int:
    if len(a) != len(b):
        return -1
    return [x + y for (x, y) in zip(a, b)]

import numpy as np
def compute_policy_gradient(theta: np.ndarray, episodes: list[list[tuple[int, int, float]]]):
    (num_states, num_actions) = theta.shape
    gradient = np.zeros_like(theta)
    for episode in episodes:
        G = 0
        returns = []
        for (state, action, reward) in reversed(episode):
            G = reward + G
            returns.append((state, action, G))
        returns.reverse()
        for (state, action, G) in returns:
            probs = np.exp(theta[state, :]) / np.sum(np.exp(theta[state, :]))
            gradient[state, action] += G * (1 - probs[action])
            for a in range(num_actions):
                if a != action:
                    gradient[state, a] -= G * probs[a]
    gradient /= len(episodes)
    gradient = np.round(gradient, 4)
    return gradient.tolist()

import numpy as np
def find_treasure(start_x: float, learning_rate: float=0.1, tolerance: float=1e-06, max_iters: int=10000) -> float:

    def f(x):
        return x ** 4 - 3 * x ** 3 + 2

    def df(x):
        return 4 * x ** 3 - 9 * x ** 2
    x = start_x
    for _ in range(max_iters):
        gradient = df(x)
        new_x = x - learning_rate * gradient
        if abs(new_x - x) < tolerance:
            break
        x = new_x
    return round(x, 4)
