def solve_jacobi(A: np.ndarray, b: np.ndarray, n: int) -> list:
    if A.shape[0] != A.shape[1]:
        raise ValueError('Matrix A must be a square matrix.')
    if A.shape[0] != b.shape[0]:
        raise ValueError('The number of rows in A must match the number of elements in b.')
    size = A.shape[0]
    x = np.zeros(size, dtype=float)
    for _ in range(n):
        x_new = np.zeros(size, dtype=float)
        for i in range(size):
            s = sum((A[i][j] * x[j] for j in range(size) if i != j))
            x_new[i] = (b[i] - s) / A[i][i]
        x_new = np.round(x_new, 4)
        x = x_new
    return x.tolist()
