def learn_decision_tree(examples: list[dict], attributes: list[str], target_attr: str) -> dict:

    def entropy(class_counts: dict) -> float:
        total = sum(class_counts.values())
        return -sum((count / total * math.log2(count / total) for count in class_counts.values() if count > 0))

    def information_gain(examples: list[dict], attr: str) -> float:
        class_counts = Counter((example[target_attr] for example in examples))
        total_entropy = entropy(class_counts)
        attr_values = set((example[attr] for example in examples))
        attr_entropy = 0.0
        for value in attr_values:
            subset = [example for example in examples if example[attr] == value]
            subset_entropy = entropy(Counter((example[target_attr] for example in subset)))
            attr_entropy += len(subset) / len(examples) * subset_entropy
        return total_entropy - attr_entropy

    def majority_value(examples: list[dict]) -> str:
        return Counter((example[target_attr] for example in examples)).most_common(1)[0][0]

    def decision_tree_learning(examples: list[dict], attributes: list[str], default: str) -> dict:
        if not examples:
            return default
        elif all((example[target_attr] == examples[0][target_attr] for example in examples)):
            return examples[0][target_attr]
        elif not attributes:
            return majority_value(examples)
        else:
            best_attr = max(attributes, key=lambda attr: information_gain(examples, attr))
            tree = {best_attr: {}}
            for value in set((example[best_attr] for example in examples)):
                subset = [example for example in examples if example[best_attr] == value]
                subtree = decision_tree_learning(subset, [a for a in attributes if a != best_attr], majority_value(examples))
                tree[best_attr][value] = subtree
            return tree
    return decision_tree_learning(examples, attributes, majority_value(examples))
