def pegasos_kernel_svm(data: np.ndarray, labels: np.ndarray, kernel='linear', lambda_val=0.01, iterations=100, sigma=1.0):
    labels = np.where(labels == 0, -1, labels)
    (n_samples, n_features) = data.shape
    alphas = np.zeros(n_samples)
    bias = 0.0

    def linear_kernel(x1, x2):
        return np.dot(x1, x2)

    def rbf_kernel(x1, x2, sigma):
        return np.exp(-np.linalg.norm(x1 - x2) ** 2 / (2 * sigma ** 2))
    kernel_func = linear_kernel if kernel == 'linear' else lambda x1, x2: rbf_kernel(x1, x2, sigma)
    for t in range(1, iterations + 1):
        learning_rate = 1 / (lambda_val * t)
        gradient = np.zeros(n_samples)
        for i in range(n_samples):
            prediction = 0
            for j in range(n_samples):
                prediction += alphas[j] * labels[j] * kernel_func(data[i], data[j])
            prediction += bias
            gradient[i] = -labels[i] * prediction
        for i in range(n_samples):
            if gradient[i] < 1:
                alphas[i] = min(1 / (lambda_val * n_samples) * (alphas[i] - learning_rate * gradient[i]), 1 / (lambda_val * n_samples))
            else:
                alphas[i] = 0
        bias -= learning_rate * np.sum(gradient)
    alphas = np.round(alphas, 4)
    bias = np.round(bias, 4)
    return (alphas.tolist(), bias.tolist())
