class Dense(Layer):

    def __init__(self, n_units, input_shape=None):
        self.layer_input = None
        self.input_shape = input_shape
        self.n_units = n_units
        self.trainable = True
        self.W = None
        self.w0 = None
        self.W_opt = SgdOptimizer()
        self.w0_opt = SgdOptimizer()

    def initialize(self, optimizer=None):
        limit = 1 / math.sqrt(self.input_shape[0])
        self.W = np.random.uniform(-limit, limit, (self.input_shape[0], self.n_units))
        self.w0 = np.zeros((1, self.n_units))
        if optimizer is not None:
            self.W_opt = copy.deepcopy(optimizer)
            self.w0_opt = copy.deepcopy(optimizer)

    def parameters(self):
        return np.prod(self.W.shape) + np.prod(self.w0.shape)

    def forward_pass(self, X, training=True):
        self.layer_input = X
        return np.dot(X, self.W) + self.w0

    def backward_pass(self, accum_grad):
        W = self.W
        if self.trainable:
            grad_w = np.dot(self.layer_input.T, accum_grad)
            grad_w0 = np.sum(accum_grad, axis=0, keepdims=True)
            self.W = self.W_opt.update_params([(self.W, grad_w)])[0]
            self.w0 = self.w0_opt.update_params([(self.w0, grad_w0)])[0]
        grad_input = np.dot(accum_grad, W.T)
        return grad_input

    def output_shape(self):
        return (self.n_units,)
