def gradient_descent(X, y, weights, learning_rate, n_iterations, batch_size=1, method='batch'):
    X = np.array(X)
    y = np.array(y)
    weights = np.array(weights, dtype=float)

    def compute_mse(y_true, y_pred):
        return np.mean((y_true - y_pred) ** 2)

    def compute_gradient(X, y_true, y_pred):
        return 2 / X.shape[0] * X.T.dot(y_pred - y_true)
    n_samples = X.shape[0]
    for _ in range(n_iterations):
        if method == 'batch':
            X_batch = X
            y_batch = y
        elif method == 'stochastic':
            index = np.random.randint(n_samples)
            X_batch = X[index:index + 1]
            y_batch = y[index:index + 1]
        elif method == 'mini-batch':
            indices = np.random.choice(n_samples, batch_size, replace=False)
            X_batch = X[indices]
            y_batch = y[indices]
        else:
            raise ValueError("Method should be 'batch', 'stochastic', or 'mini-batch'")
        y_pred = X_batch.dot(weights)
        gradient = compute_gradient(X_batch, y_batch, y_pred)
        weights -= learning_rate * gradient
    return weights.round(4).tolist()
