def compute_tf_idf(corpus, query):
    if not corpus:
        return []
    unique_docs = [set(doc) for doc in corpus]
    df = {word: sum((1 for doc in unique_docs if word in doc)) for word in set((word for doc in corpus))}
    num_docs = len(corpus)
    idf = {word: np.log((num_docs + 1) / (df[word] + 1)) for word in df}
    tf_idf_scores = []
    for doc in corpus:
        tf = {word: doc.count(word) for word in doc}
        doc_tf_idf = []
        for word in query:
            tf_value = tf.get(word, 0)
            idf_value = idf.get(word, 0)
            doc_tf_idf.append(round(tf_value * idf_value, 4))
        tf_idf_scores.append(doc_tf_idf)
    return np.array(tf_idf_scores).tolist()
