def layer_norm(x, g, b, eps: float=1e-05):
    mu = np.mean(x, axis=-1, keepdims=True)
    var = np.var(x, axis=-1, keepdims=True)
    x = (x - mu) / np.sqrt(var + eps)
    return g * x + b
def multi_head_attention(q, k, v, n_head: int):
    (batch_size, seq_len, d_model) = q.shape
    d_k = d_model // n_head
    q = q.reshape(batch_size, seq_len, n_head, d_k).transpose(0, 2, 1, 3)
    k = k.reshape(batch_size, seq_len, n_head, d_k).transpose(0, 2, 1, 3)
    v = v.reshape(batch_size, seq_len, n_head, d_k).transpose(0, 2, 1, 3)
    scores = np.matmul(q, k.transpose(0, 1, 3, 2)) / np.sqrt(d_k)
    weights = np.exp(scores - np.max(scores, axis=-1, keepdims=True))
    weights = weights / np.sum(weights, axis=-1, keepdims=True)
    context = np.matmul(weights, v).transpose(0, 2, 1, 3).reshape(batch_size, seq_len, d_model)
    return context
def feed_forward(x, d_model: int, d_ff: int):
    w1 = np.random.rand(d_model, d_ff)
    b1 = np.random.rand(d_ff)
    w2 = np.random.rand(d_ff, d_model)
    b2 = np.random.rand(d_model)
    return np.matmul(np.maximum(np.matmul(x, w1) + b1, 0), w2) + b2
def gen_text(prompt: str, n_tokens_to_generate: int=40):
    (encoder, hparams, params) = load_encoder_hparams_and_params()
    d_model = params['wte'].shape[1]
    n_head = hparams['n_head']
    context_len = hparams['n_ctx']
    input_ids = encoder.encode(prompt)
    input_ids = input_ids[:context_len]
    input_ids = np.array(input_ids).reshape(1, -1)
    for _ in range(n_tokens_to_generate):
        token_embeddings = params['wte'][input_ids]
        positions = np.arange(input_ids.shape[1])
        positional_embeddings = params['wpe'][positions]
        x = token_embeddings + positional_embeddings
        for _ in params['blocks']:
            attn_output = multi_head_attention(x, x, x, n_head)
            attn_output = layer_norm(x + attn_output, params['ln_f']['g'], params['ln_f']['b'])
            ffn_output = feed_forward(attn_output, d_model, d_model * 4)
            x = layer_norm(attn_output + ffn_output, params['ln_f']['g'], params['ln_f']['b'])
        logits = np.matmul(x[:, -1], params['wte'].T)
        next_token = np.argmax(logits, axis=-1)
        input_ids = np.concatenate([input_ids, next_token.reshape(1, 1)], axis=1)
        if input_ids.shape[1] > context_len:
            input_ids = input_ids[:, -context_len:]
    generated_text = encoder.decode(input_ids[0])
    return generated_text
