def find_treasure(start_x: float, learning_rate: float=0.1, tolerance: float=1e-06, max_iters: int=10000) -> float:

    def f(x):
        return x ** 4 - 3 * x ** 3 + 2

    def f_prime(x):
        return 4 * x ** 3 - 9 * x ** 2
    current_x = start_x
    for i in range(max_iters):
        gradient = f_prime(current_x)
        new_x = current_x - learning_rate * gradient
        if abs(new_x - current_x) < tolerance:
            break
        current_x = new_x
    return round(current_x, 4)
