def solve_jacobi(A: np.ndarray, b: np.ndarray, n: int) -> list:
    if A.shape[0] != A.shape[1]:
        raise ValueError('Matrix A must be a square matrix.')
    if A.shape[0] != b.shape[0]:
        raise ValueError('The number of rows in A must match the length of b.')
    n_vars = A.shape[0]
    x = np.zeros(n_vars)
    for _ in range(n):
        x_new = np.zeros(n_vars)
        for i in range(n_vars):
            s1 = np.dot(A[i, :i], x[:i])
            s2 = np.dot(A[i, i + 1:], x[i + 1:])
            x_new[i] = (b[i] - s1 - s2) / A[i, i]
        x = x_new.round(4)
    return x.tolist()

def determinant_4x4(matrix: list[list[int | float]]) -> float:

    def determinant_3x3(minor: list[list[int | float]]) -> float:
        (a, b, c) = minor[0]
        (d, e, f) = minor[1]
        (g, h, i) = minor[2]
        return a * (e * i - f * h) - b * (d * i - f * g) + c * (d * h - e * g)
    if len(matrix) != 4 or any((len(row) != 4 for row in matrix)):
        raise ValueError('The input must be a 4x4 matrix.')
    det = 0
    for c in range(4):
        minor = [row[:c] + row[c + 1:] for row in matrix[1:]]
        det += matrix[0][c] * (-1) ** c * determinant_3x3(minor)
    return det
