from collections import Counter
def learn_decision_tree(examples: list[dict], attributes: list[str], target_attr: str) -> dict:

    def entropy(class_counts: Counter) -> float:
        total = sum(class_counts.values())
        return -sum((count / total * math.log2(count / total) for count in class_counts.values() if count > 0))

    def information_gain(examples: list[dict], attribute: str, target_attr: str) -> float:
        total_entropy = entropy(Counter((example[target_attr] for example in examples)))
        values = set((example[attribute] for example in examples))
        weighted_entropy = 0
        for value in values:
            subset = [example for example in examples if example[attribute] == value]
            weighted_entropy += len(subset) / len(examples) * entropy(Counter((example[target_attr] for example in subset)))
        return total_entropy - weighted_entropy

    def most_common_class(examples: list[dict], target_attr: str) -> str:
        return Counter((example[target_attr] for example in examples)).most_common(1)[0][0]

    def build_tree(examples: list[dict], attributes: list[str], target_attr: str) -> dict:
        class_counts = Counter((example[target_attr] for example in examples))
        if len(class_counts) == 1:
            return list(class_counts.keys())[0]
        if not attributes:
            return most_common_class(examples, target_attr)
        best_attribute = max(attributes, key=lambda attr: information_gain(examples, attr, target_attr))
        tree = {best_attribute: {}}
        for value in set((example[best_attribute] for example in examples)):
            subset = [example for example in examples if example[best_attribute] == value]
            if not subset:
                tree[best_attribute][value] = most_common_class(examples, target_attr)
            else:
                remaining_attributes = [attr for attr in attributes if attr != best_attribute]
                tree[best_attribute][value] = build_tree(subset, remaining_attributes, target_attr)
        return tree
    return build_tree(examples, attributes, target_attr)
