import numpy as np
def pegasos_kernel_svm(data: np.ndarray, labels: np.ndarray, kernel='linear', lambda_val=0.01, iterations=100, sigma=1.0):
    """
    Implements a deterministic version of the Pegasos algorithm for training a kernel SVM classifier.

    Parameters:
    - data: A 2D NumPy array of shape (n_samples, n_features) representing the dataset.
    - labels: A 1D NumPy array of shape (n_samples,) representing the sample labels.
    - kernel: A string specifying the kernel type ('linear' or 'RBF'). Default is 'linear'.
    - lambda_val: A float representing the regularization parameter. Default is 0.01.
    - iterations: An integer representing the number of iterations to perform. Default is 100.
    - sigma: A float representing the standard deviation for the RBF kernel. Default is 1.0.

    Returns:
    - A list containing the alpha coefficients and bias, rounded to the nearest 4th decimal.
    """
    (n_samples, n_features) = data.shape
    alphas = np.zeros(n_samples)
    bias = 0.0

    def kernel_function(x1, x2):
        if kernel == 'linear':
            return np.dot(x1, x2)
        elif kernel == 'RBF':
            return np.exp(-np.linalg.norm(x1 - x2) ** 2 / (2 * sigma ** 2))
        else:
            raise ValueError('Unsupported kernel type')
    K = np.zeros((n_samples, n_samples))
    for i in range(n_samples):
        for j in range(n_samples):
            K[i, j] = kernel_function(data[i], data[j])
    for t in range(1, iterations + 1):
        step_size = 1.0 / (lambda_val * t)
        for i in range(n_samples):
            prediction = np.sum(alphas * labels * K[:, i]) + bias
            if labels[i] * prediction < 1:
                alphas[i] += step_size * labels[i]
                bias += step_size * labels[i]
            alphas = np.maximum(0, (1 - 1.0 / (lambda_val * t)) * alphas)
    result = np.concatenate((alphas, [bias]))
    return result.round(4).tolist()

