def train_neuron(features: np.ndarray, labels: np.ndarray, initial_weights: np.ndarray, initial_bias: float, learning_rate: float, epochs: int) -> (np.ndarray, float, list[float]):
    weights = initial_weights
    bias = initial_bias
    mse_history = []
    for epoch in range(epochs):
        weighted_sum = np.dot(features, weights) + bias
        predictions = sigmoid(weighted_sum)
        mse = mean_squared_error(predictions, labels)
        mse_history.append(round(mse, 4))
        error = predictions - labels
        d_loss_d_pred = 2 * error / len(labels)
        d_pred_d_z = sigmoid_derivative(predictions)
        d_loss_d_z = d_loss_d_pred * d_pred_d_z
        d_loss_d_w = np.dot(features.T, d_loss_d_z)
        d_loss_d_b = np.sum(d_loss_d_z)
        weights -= learning_rate * d_loss_d_w
        bias -= learning_rate * d_loss_d_b
    return (weights.tolist(), round(bias, 4), mse_history)

class Value:

    def __init__(self, data, _children=(), _op=''):
        self.data = data
        self.grad = 0
        self._backward = lambda : None
        self._prev = set(_children)
        self._op = _op

    def __add__(self, other):
        other = other if isinstance(other, Value) else Value(other)
        out = Value(self.data + other.data, (self, other), '+')

        def _backward():
            self.grad += out.grad
            other.grad += out.grad
        out._backward = _backward
        return out

    def __mul__(self, other):
        other = other if isinstance(other, Value) else Value(other)
        out = Value(self.data * other.data, (self, other), '*')

        def _backward():
            self.grad += other.data * out.grad
            other.grad += self.data * out.grad
        out._backward = _backward
        return out

    def relu(self):
        out = Value(0 if self.data < 0 else self.data, (self,), 'ReLU')

        def _backward():
            self.grad += (out.data > 0) * out.grad
        out._backward = _backward
        return out

    def backward(self):
        topo = []
        visited = set()

        def build_topo(v):
            if v not in visited:
                visited.add(v)
                for child in v._prev:
                    build_topo(child)
                topo.append(v)
        build_topo(self)
        self.grad = 1
        for node in reversed(topo):
            node._backward()

    def __repr__(self):
        return f'Value(data={self.data}, grad={self.grad})'

