        self.grad_w0 = np.sum(accum_grad, axis=0, keepdims=True)
        if self.trainable:
            self.W = self.optimizer.update(self.W, self.grad_W)
            self.w0 = self.optimizer_w0.update(self.w0, self.grad_w0)
        return np.dot(accum_grad, self.W.T)

