def rref(matrix):
    A = np.array(matrix, dtype=float)
    (rows, cols) = A.shape
    rank = 0
    for col in range(cols):
        if rank < rows:
            pivot_row = None
            for r in range(rank, rows):
                if A[r, col] != 0:
                    pivot_row = r
                    break
            if pivot_row is not None:
                A[[rank, pivot_row]] = A[[pivot_row, rank]]
                pivot = A[rank, col]
                A[rank] /= pivot
                for r in range(rows):
                    if r != rank:
                        factor = A[r, col]
                        A[r] -= factor * A[rank]
                rank += 1
    return A.tolist()
