def gauss_seidel(A, b, n, x_ini=None):
    A = np.array(A)
    b = np.array(b)
    N = len(A)
    if A.shape != (N, N):
        raise ValueError('Matrix A must be a square matrix.')
    if b.shape != (N,):
        raise ValueError('Vector b must have the same length as the dimension of matrix A.')
    if x_ini is None:
        x = np.zeros(N)
    else:
        x = np.array(x_ini)
        if x.shape != (N,):
            raise ValueError('Initial guess x_ini must have the same length as the dimension of matrix A.')
    for _ in range(n):
        x_new = np.copy(x)
        for i in range(N):
            sigma = 0.0
            for j in range(N):
                if i != j:
                    sigma += A[i][j] * x_new[j]
            x_new[i] = (b[i] - sigma) / A[i][i]
        x = x_new
    return np.round(x, 4).tolist()

def compressed_col_sparse_matrix(dense_matrix):
    """
    Convert a dense matrix into its Compressed Column Sparse (CSC) representation.

    :param dense_matrix: List of lists representing the dense matrix
    :return: Tuple of (values, row indices, column pointer)
    """
    if not dense_matrix or not dense_matrix[0]:
        return ([], [], [0])
