def create_row_hv(row, dim, random_seeds):
    """Create a composite hypervector for a given dataset row using HDC."""
    composite_hv = np.zeros(dim)
    for (feature, value) in row.items():
        feature_seed = random_seeds.get(feature, None)
        if feature_seed is None:
            raise ValueError(f"Seed for feature '{feature}' not provided in random_seeds.")
        feature_hv = create_hv(dim, feature_seed)
        value_seed = hash((feature_seed, value))
