def model_fit_quality(training_accuracy, test_accuracy):
    """
    Determine if the model is overfitting, underfitting, or a good fit based on training and test accuracy.
    :param training_accuracy: float, training accuracy of the model (0 <= training_accuracy <= 1)
    :param test_accuracy: float, test accuracy of the model (0 <= test_accuracy <= 1)
    :return: int, one of '1', '-1', or '0'.
    """
    if training_accuracy - test_accuracy > 0.2:
        return 1
    elif training_accuracy < 0.7 and test_accuracy < 0.7:
        return -1
    else:
        return 0

import math
def softplus(x: float) -> float:
    """
    Compute the softplus activation function.

    Args:
        x: Input value

    Returns:
        The softplus value: log(1 + e^x)
    """
    if x > 20:
        return round(x, 4)
    elif x < -20:
        return round(math.log1p(math.exp(x)), 4)
    else:
        return round(math.log1p(math.exp(x)), 4)

def min_max(x: list[int]) -> list[float]:
    if not x:
        return []
    min_val = min(x)
    max_val = max(x)
    if min_val == max_val:
        return [0.0] * len(x)
    normalized = [(val - min_val) / (max_val - min_val) for val in x]
    return [round(val, 4) for val in normalized]

import numpy as np
def batch_normalization(X: np.ndarray, gamma: np.ndarray, beta: np.ndarray, epsilon: float=1e-05):
    (batch_size, channels, height, width) = X.shape
    X_reshaped = X.reshape(batch_size, channels, -1)
    mean = np.mean(X_reshaped, axis=(0, 2), keepdims=True)
    var = np.var(X_reshaped, axis=(0, 2), keepdims=True)
    X_normalized = (X_reshaped - mean) / np.sqrt(var + epsilon)
    X_normalized = X_normalized.reshape(batch_size, channels, height, width)
    X_normalized = gamma.reshape(1, channels, 1, 1) * X_normalized + beta.reshape(1, channels, 1, 1)
    return X_normalized.round(4).tolist()

import numpy as np
def cross_product(a, b):
    result = np.cross(a, b)
    return np.round(result, 4).tolist()

import numpy as np
def bhattacharyya_distance(p: list[float], q: list[float]) -> float:
    if len(p) != len(q) or not p:
        return 0.0
    p = np.array(p)
    q = np.array(q)
    bc = np.sum(np.sqrt(p * q))
    bd = -np.log(bc) if bc > 0 else float('inf')
    return round(bd, 4)

def vector_sum(a: list[int | float], b: list[int | float]) -> list[int | float]:
    if len(a) != len(b):
        return -1
    return [x + y for (x, y) in zip(a, b)]

import numpy as np
def find_treasure(start_x: float, learning_rate: float=0.1, tolerance: float=1e-06, max_iters: int=10000) -> float:

    def f(x):
        return x ** 4 - 3 * x ** 3 + 2

    def f_prime(x):
        return 4 * x ** 3 - 9 * x ** 2
    current_x = start_x
    for i in range(max_iters):
        gradient = f_prime(current_x)
        new_x = current_x - learning_rate * gradient
        if abs(new_x - current_x) < tolerance:
            break
        current_x = new_x
    return round(current_x, 4)
