def softplus(x, deriv: bool=False):
    """Compute the SoftPlus activation or its gradient.

    Args:
        x (scalar, list, or np.ndarray): Input values.
        deriv (bool): If True, compute the gradient of SoftPlus.

    Returns:
        list or float: SoftPlus values or their gradients, rounded to 4 decimal places.
    """
    x_arr = np.array(x, dtype=float)
    if deriv:
        result = 1 / (1 + np.exp(-x_arr))
    else:
        result = np.log1p(np.exp(-np.abs(x_arr))) + np.maximum(x_arr, 0)
    rounded_result = np.round(result, 4)
    if isinstance(x, (int, float)):
        return float(rounded_result)
    else:
        return rounded_result.tolist()

