import math
def adaboost_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, n_clf: int=5) -> list[int]:
    """Train an AdaBoost ensemble of decision stumps and predict labels for X_test.

    Args:
        X_train: 2-D numpy array of shape (n_samples, n_features) with training data.
        y_train: 1-D numpy array of shape (n_samples,) containing class labels (-1 or 1).
        X_test:  2-D numpy array of unseen samples to classify.
        n_clf:   Number of weak learners (decision stumps) to use in the ensemble.

    Returns:
        A Python list with one element per row in *X_test*. Each element must be
        either -1 or 1 indicating the predicted class label.
    """

    def decision_stump_predict(X, feature_index, threshold, polarity):
        predictions = np.ones(X.shape[0])
        if polarity == 1:
            predictions[X[:, feature_index] < threshold] = -1
        else:
            predictions[X[:, feature_index] >= threshold] = -1
        return predictions
    (n_samples, n_features) = X_train.shape
    sample_weights = np.ones(n_samples) / n_samples
    classifiers = []
    for _ in range(n_clf):
        min_error = float('inf')
        best_clf = {}
        for feature_index in range(n_features):
            thresholds = np.unique(X_train[:, feature_index])
            for threshold in thresholds:
                for polarity in [1, -1]:
                    predictions = decision_stump_predict(X_train, feature_index, threshold, polarity)
                    weighted_error = np.sum(sample_weights[predictions != y_train])
                    if weighted_error > 0.5:
                        weighted_error = 1 - weighted_error
                        polarity = -polarity
                    if weighted_error < min_error:
                        min_error = weighted_error
                        best_clf = {'feature_index': feature_index, 'threshold': threshold, 'polarity': polarity}
        alpha = 0.5 * math.log((1 - min_error) / (min_error + 1e-10))
        classifiers.append((alpha, best_clf))
        predictions = decision_stump_predict(X_train, best_clf['feature_index'], best_clf['threshold'], best_clf['polarity'])
        sample_weights *= np.exp(-alpha * y_train * predictions)
        sample_weights /= np.sum(sample_weights)

    def ensemble_predict(X):
        ensemble_predictions = np.zeros(X.shape[0])
        for (alpha, clf) in classifiers:
            predictions = decision_stump_predict(X, clf['feature_index'], clf['threshold'], clf['polarity'])
            ensemble_predictions += alpha * predictions
        return np.sign(ensemble_predictions)
    return ensemble_predict(X_test).astype(int).tolist()

import numpy as np
from dataclasses import dataclass
@dataclass
class Leaf:
    """A terminal node that stores a prediction value."""
    value: object
@dataclass
class Node:
    """An internal decision-tree node.

    Attributes:
        feature (int):   Index of the feature to test.
        threshold (float): Threshold that splits the data.
        left (Node | Leaf):  Sub-tree for samples with feature value < threshold.
        right (Node | Leaf): Sub-tree for samples with feature value ≥ threshold.
    """
    feature: int
    threshold: float
    left: object
    right: object
def compare_trees(tree_a, tree_b):
    """Recursively checks whether *tree_a* and *tree_b* are equivalent.

    Args:
        tree_a: Root node of the first decision tree (Node or Leaf).
        tree_b: Root node of the second decision tree (Node or Leaf).

    Returns:
        True if the two trees are equivalent, False otherwise.
    """
    if isinstance(tree_a, Leaf) and isinstance(tree_b, Leaf):
        return np.allclose(tree_a.value, tree_b.value, atol=1e-08)
    elif isinstance(tree_a, Node) and isinstance(tree_b, Node):
        return tree_a.feature == tree_b.feature and np.isclose(tree_a.threshold, tree_b.threshold, atol=1e-08) and compare_trees(tree_a.left, tree_b.left) and compare_trees(tree_a.right, tree_b.right)
    else:
        return False

import numpy as np
def spectral_clustering(X: np.ndarray, k: int) -> list[int]:
    """Cluster data using Spectral Clustering.

    Args:
        X: 2-D NumPy array where each row is a data sample.
        k: Desired number of clusters.

    Returns:
        List of length n_samples containing an integer cluster label for each sample.
    """
    (n, d) = X.shape
    W = np.zeros((n, n))
    for i in range(n):
        for j in range(n):
            if i != j:
                W[i, j] = 1 / (1 + np.linalg.norm(X[i] - X[j]))
    D = np.diag(np.sum(W, axis=1))
    L = D - W
    (eigenvalues, eigenvectors) = np.linalg.eigh(L)
    E = eigenvectors[:, :k]
    centroids = E[:k].copy()
    labels = np.zeros(n, dtype=int)
    for _ in range(100):
        for i in range(n):
            distances = np.linalg.norm(E[i] - centroids, axis=1)
            labels[i] = np.argmin(distances)
        new_centroids = np.zeros_like(centroids)
        counts = np.zeros(k, dtype=int)
        for i in range(n):
            new_centroids[labels[i]] += E[i]
            counts[labels[i]] += 1
        for j in range(k):
            if counts[j] == 0:
                new_centroids[j] = E[np.random.randint(n)]
            else:
                new_centroids[j] /= counts[j]
        if np.all(centroids == new_centroids):
            break
        centroids = new_centroids
    return labels.tolist()

import numpy as np
def mel_filterbank(N: int, n_filters: int=20, fs: int=44000, min_freq: int=0, max_freq: int | None=None, normalize: bool=True) -> list[list[float]]:
    """Build a Mel filterbank transformation matrix.

    The returned matrix has *n_filters* rows and *N//2 + 1* columns.  Each row
    is a triangular filter defined on the Mel scale.  See the detailed task
    description for the exact algorithm that has to be implemented.
    """
    if max_freq is None:
        max_freq = fs / 2

    def hz_to_mel(f):
        return 2595 * np.log10(1 + f / 700)

    def mel_to_hz(mel):
        return 700 * (10 ** (mel / 2595) - 1)
    min_mel = hz_to_mel(min_freq)
    max_mel = hz_to_mel(max_freq)
    mel_points = np.linspace(min_mel, max_mel, n_filters + 2)
    hz_points = mel_to_hz(mel_points)
    filterbank = np.zeros((n_filters, N // 2 + 1))
    for i in range(n_filters):
        f_left = hz_points[i]
        f_center = hz_points[i + 1]
        f_right = hz_points[i + 2]
        for k in range(N // 2 + 1):
            f_k = k * fs / N
            if f_left < f_k <= f_center:
                filterbank[i, k] = (f_k - f_left) / (f_center - f_left)
            elif f_center < f_k <= f_right:
                filterbank[i, k] = (f_right - f_k) / (f_right - f_center)
        if normalize:
            filterbank[i] *= 2 / (f_right - f_left)
    filterbank = np.round(filterbank, 4).tolist()
    return filterbank

from typing import Any, Dict, Iterable, Tuple as PyTuple
class Space:
    pass
class Box(Space):

    def __init__(self, low: float, high: float, shape: PyTuple[int, ...]):
        self.low = low
        self.high = high
        self.shape = shape
class Tuple(Space):

    def __init__(self, spaces: Iterable[Space]):
        self.spaces = tuple(spaces)
class Dict(Space):

    def __init__(self, spaces: Dict[str, Space]):
        self.spaces = dict(spaces)
class Env:
    """Tiny environment that only stores two spaces."""

    def __init__(self, action_space: Space, observation_space: Space):
        self.action_space = action_space
        self.observation_space = observation_space
def is_continuous(env: Env, tuple_action: bool, tuple_obs: bool) -> Tuple[bool, bool]:
    """Determine whether the given environment's spaces are continuous.

    A space is *continuous* if it is an instance of `Box`. For composite spaces
    (`Tuple` or `Dict`) the space is continuous only if **all** its sub-spaces
    are `Box`.

    Args:
        env:          Environment exposing `action_space` and `observation_space`.
        tuple_action: Whether the *action* space is composite.
        tuple_obs:    Whether the *observation* space is composite.

    Returns:
        A tuple `(cont_action, cont_obs)` of booleans.
    """

    def is_box_space(space: Space) -> bool:
        if isinstance(space, Box):
            return True
        elif isinstance(space, (Tuple, Dict)):
            sub_spaces = space.spaces if isinstance(space, Dict) else space.spaces
            return all((is_box_space(sub_space) for sub_space in sub_spaces))
        else:
            return False
    cont_action = is_box_space(env.action_space) if tuple_action else isinstance(env.action_space, Box)
    cont_obs = is_box_space(env.observation_space) if tuple_obs else isinstance(env.observation_space, Box)
    return (cont_action, cont_obs)

import numpy as np
def gbdt_regression(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, n_estimators: int=10, learning_rate: float=0.1, max_depth: int=3) -> list[float]:
    """Gradient Boosting Decision Tree (GBDT) regressor.

    Parameters
    ----------
    X_train : np.ndarray
        2-D array with shape (n_samples, n_features) containing the training
        features.
    y_train : np.ndarray
        1-D array of length n_samples containing the training targets.
    X_test : np.ndarray
        2-D array with shape (m_samples, n_features) containing the test
        features to predict.
    n_estimators : int, default=10
        Number of boosting iterations.
    learning_rate : float, default=0.1
        Shrinkage factor applied to each tree’s prediction.
    max_depth : int, default=3
        Maximum depth of every individual regression tree.

    Returns
    -------
    list[float]
        Predictions for every sample in *X_test*, rounded to 4 decimal places.
    """

    def mean_squared_error(y_true, y_pred):
        return np.mean((y_true - y_pred) ** 2)

    def best_split(X, y):
        (best_feature, best_threshold, best_mse) = (None, None, float('inf'))
        (n_samples, n_features) = X.shape
        for feature in range(n_features):
            thresholds = np.unique(X[:, feature])
            for threshold in thresholds:
                left_indices = X[:, feature] < threshold
                right_indices = X[:, feature] >= threshold
                if np.any(left_indices) and np.any(right_indices):
                    (y_left, y_right) = (y[left_indices], y[right_indices])
                    mse = (len(y_left) * mean_squared_error(y_left, np.mean(y_left)) + len(y_right) * mean_squared_error(y_right, np.mean(y_right))) / n_samples
                    if mse < best_mse:
                        best_mse = mse
                        best_feature = feature
                        best_threshold = threshold
        return (best_feature, best_threshold)

    def build_tree(X, y, depth=0):
        if depth >= max_depth or len(np.unique(y)) == 1:
            return np.mean(y)
        (best_feature, best_threshold) = best_split(X, y)
        if best_feature is None:
            return np.mean(y)
        left_indices = X[:, best_feature] < best_threshold
        right_indices = X[:, best_feature] >= best_threshold
        left_tree = build_tree(X[left_indices], y[left_indices], depth + 1)
        right_tree = build_tree(X[right_indices], y[right_indices], depth + 1)
        return (best_feature, best_threshold, left_tree, right_tree)

    def predict_tree(tree, X):
        if isinstance(tree, float):
            return tree
        (feature, threshold, left_tree, right_tree) = tree
        if X[feature] < threshold:
            return predict_tree(left_tree, X)
        else:
            return predict_tree(right_tree, X)

    def predict_trees(trees, X):
        return np.array([sum((predict_tree(tree, x) for tree in trees)) for x in X])
    y_pred = np.full_like(y_train, np.mean(y_train))
    trees = []
    for _ in range(n_estimators):
        residuals = y_train - y_pred
        tree = build_tree(X_train, residuals)
        trees.append(tree)
        y_pred += learning_rate * predict_trees([tree], X_train)
    final_predictions = predict_trees(trees, X_test) + np.mean(y_train)
    return [round(pred, 4) for pred in final_predictions]

import numpy as np
def root_mean_squared_log_error(actual: list[float], predicted: list[float]) -> float:
    """Calculate the Root Mean Squared Logarithmic Error (RMSLE) between actual and predicted values."""
    if len(actual) != len(predicted):
        return -1
    if any((a < 0 for a in actual)) or any((p < 0 for p in predicted)):
        return -1
    n = len(actual)
    rmsle = np.sqrt(1 / n * np.sum((np.log1p(predicted) - np.log1p(actual)) ** 2))
    return round(rmsle, 4)

import numpy as np
def sigmoid(x, order: int=0):
    """Compute the logistic sigmoid or its derivatives.

    Args:
        x (float | int | list | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        order (int, optional): 0 = function value, 1 = first derivative,
            2 = second derivative. Defaults to 0.

    Returns:
        float | list: Result rounded to 4 decimals. Scalar input returns a float;
            vector/matrix input returns a Python list preserving the shape.
            If *order* is not 0, 1, or 2, the function returns -1.
    """
    is_scalar = isinstance(x, (int, float))
    x = np.array(x, dtype=float)
    sigma = 1 / (1 + np.exp(-x))
    if order == 0:
        result = sigma
    elif order == 1:
        result = sigma * (1 - sigma)
    elif order == 2:
        result = sigma * (1 - sigma) * (1 - 2 * sigma)
    else:
        return -1
    result = np.round(result, 4)
    if is_scalar:
        return float(result)
    else:
        return result.tolist()

import itertools
from typing import List, Tuple, Hashable
def apriori_frequent_itemsets(transactions: List[List[Hashable]], min_sup: float) -> List[Tuple]:
    """Find frequent itemsets with the Apriori algorithm.

    Parameters
    ----------
    transactions : list[list[Hashable]]
        List of transactions; each transaction is itself a list of items.
    min_sup : float
        Minimum support threshold expressed as a fraction (>0 and ≤1).

    Returns
    -------
    list[tuple]
        Frequent itemsets ordered by length and then lexicographically.
    """

    def support(itemset: Tuple, transactions: List[List[Hashable]]) -> float:
        return sum((1 for transaction in transactions if set(itemset).issubset(set(transaction)))) / len(transactions)

    def generate_candidates(frequent_itemsets: List[Tuple], k: int) -> List[Tuple]:
        candidates = set()
        for itemset1 in frequent_itemsets:
            for itemset2 in frequent_itemsets:
                if itemset1[:k - 2] == itemset2[:k - 2] and itemset1[k - 2] < itemset2[k - 2]:
                    candidate = tuple(sorted(set(itemset1) | set(itemset2)))
                    if len(candidate) == k:
                        if all((tuple(subset) in frequent_itemsets for subset in itertools.combinations(candidate, k - 1))):
                            candidates.add(candidate)
        return list(candidates)
    item_counts = {}
    for transaction in transactions:
        for item in transaction:
            item_counts[item] = item_counts.get(item, 0) + 1
    num_transactions = len(transactions)
    frequent_itemsets = [item for (item, count) in item_counts.items() if count / num_transactions >= min_sup]
    frequent_itemsets = sorted(frequent_itemsets)
    frequent_itemsets = [(item,) for item in frequent_itemsets]
    k = 2
    while frequent_itemsets:
        candidates = generate_candidates(frequent_itemsets, k)
        frequent_itemsets = [candidate for candidate in candidates if support(candidate, transactions) >= min_sup]
        frequent_itemsets.sort()
        k += 1
    all_frequent_itemsets = []
    k = 1
    while frequent_itemsets:
        all_frequent_itemsets.extend(frequent_itemsets)
        frequent_itemsets = generate_candidates(frequent_itemsets, k)
        frequent_itemsets = [candidate for candidate in frequent_itemsets if support(candidate, transactions) >= min_sup]
        frequent_itemsets.sort()
        k += 1
    return all_frequent_itemsets

import numpy as np
def rbf_kernel(X: list[list[int | float]], Y: list[list[int | float]] | None=None, sigma: float | list[float] | None=None) -> list[list[float]]:
    """Compute the Radial Basis Function (RBF) kernel matrix.

    The function should follow the specifications given in the task
    description. It must return -1 on invalid input, otherwise a nested list
    containing the kernel matrix rounded to four decimal places.
    """
    X = np.array(X)
    if Y is None:
        Y = X
    else:
        Y = np.array(Y)
    if X.shape[1] != Y.shape[1]:
        return -1
    C = X.shape[1]
    if sigma is None:
        sigma = np.sqrt(C / 2)
    elif isinstance(sigma, (int, float)):
        if sigma <= 0:
            return -1
    elif isinstance(sigma, (list, np.ndarray)):
        if len(sigma) != C or any((s <= 0 for s in sigma)):
            return -1
    else:
        return -1
    if isinstance(sigma, (int, float)):
        sigma = np.full(C, sigma)
    else:
        sigma = np.array(sigma)
    diff = X[:, np.newaxis, :] - Y[np.newaxis, :, :]
    squared_diff = (diff / sigma) ** 2
    exp_term = np.exp(-0.5 * np.sum(squared_diff, axis=2))
    kernel_matrix = np.round(exp_term, 4).tolist()
    return kernel_matrix
