import numpy as np
def regression_tree_predict(X_train: list[float | int], y_train: list[float | int], X_test: list[float | int], max_depth: int=3) -> list[float]:
    """Fit a univariate regression tree of depth *max_depth* and predict.

    Parameters
    ----------
    X_train : list[float | int]
        1-D feature values for the training set.
    y_train : list[float | int]
        Continuous target values for the training set.
    X_test : list[float | int]
        1-D feature values for which predictions are required.
    max_depth : int, default 3
        Maximum depth of the binary tree (root has depth 0).

    Returns
    -------
    list[float]
        Predictions for every element in *X_test*.
    """

    def sse(y):
        mean = np.mean(y)
        return np.sum((y - mean) ** 2)

    def best_split(X, y):
        best_sse = float('inf')
        best_split_point = None
        for i in range(1, len(X)):
            left_y = y[:i]
            right_y = y[i:]
            current_sse = sse(left_y) + sse(right_y)
            if current_sse < best_sse:
                best_sse = current_sse
                best_split_point = X[i]
        return best_split_point

    def build_tree(X, y, depth):
        if depth == max_depth or len(X) < 2:
            return np.mean(y)
        split_point = best_split(X, y)
        if split_point is None:
            return np.mean(y)
        left_indices = [i for (i, x) in enumerate(X) if x < split_point]
        right_indices = [i for (i, x) in enumerate(X) if x >= split_point]
        (left_X, left_y) = ([X[i] for i in left_indices], [y[i] for i in left_indices])
        (right_X, right_y) = ([X[i] for i in right_indices], [y[i] for i in right_indices])
        left_child = build_tree(left_X, left_y, depth + 1)
        right_child = build_tree(right_X, right_y, depth + 1)
        return (split_point, left_child, right_child)

    def predict_tree(tree, x):
        if isinstance(tree, float):
            return tree
        (split_point, left_child, right_child) = tree
        if x < split_point:
            return predict_tree(left_child, x)
        else:
            return predict_tree(right_child, x)
    if not X_train or not y_train:
        return []
    sorted_indices = np.argsort(X_train)
    X_train_sorted = [X_train[i] for i in sorted_indices]
    y_train_sorted = [y_train[i] for i in sorted_indices]
    tree = build_tree(X_train_sorted, y_train_sorted, 0)
    return [predict_tree(tree, x) for x in X_test]

import numpy as np
def sigmoid(z):
    """Compute the numerically stable, element-wise sigmoid activation.

    The function must work for a scalar, list/tuple, or NumPy ndarray input.

    Args:
        z: int, float, list, tuple, or numpy.ndarray – input values.

    Returns:
        float if *z* is a scalar, otherwise a Python list obtained with
        ``numpy.ndarray.tolist()``, where every element is the sigmoid of the
        corresponding input value and is rounded to four decimal places.
    """
    if isinstance(z, (int, float)):
        z = np.array([z])
    elif isinstance(z, (list, tuple)):
        z = np.array(z)
    else:
        z = np.asarray(z)
    sigmoid_values = np.where(z >= 0, 1 / (1 + np.exp(-z)), np.exp(z) / (1 + np.exp(z)))
    sigmoid_values = np.round(sigmoid_values, 4)
    if isinstance(z, np.ndarray) and z.size == 1:
        return float(sigmoid_values)
    else:
        return sigmoid_values.tolist()

import numpy as np
def sigmoid_grad(z):
    """Return the derivative of the sigmoid function given its output.

    The input *z* already contains sigmoid values (numbers in [0,1]).  The
    derivative is computed as z*(1-z) element-wise.

    Args:
        z: float, list or NumPy ndarray of sigmoid outputs.

    Returns:
        Same structure as *z* (float or nested list) with each value replaced
        by its gradient, rounded to 4 decimal places.
    """

    def compute_gradient(value):
        return np.round(value * (1 - value), 4)
    if isinstance(z, (int, float)):
        return compute_gradient(z)
    elif isinstance(z, list):
        if not z:
            return []
        return [sigmoid_grad(item) for item in z]
    elif isinstance(z, np.ndarray):
        return np.vectorize(compute_gradient)(z).tolist()
    else:
        raise ValueError('Input must be a float, list, or NumPy ndarray')

def predict_sample(root: 'Node', features: list | tuple, task_type: str):
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    current_node = root
    while current_node.y is None:
        if task_type == 'classification':
            feature_value = features[current_node.label]
            for child in current_node.child:
                if child.x == feature_value:
                    current_node = child
                    break
            else:
                current_node = current_node.child[1]
        elif task_type == 'regression':
            feature_value = features[current_node.label]
            if feature_value <= current_node.s:
                current_node = current_node.child[0]
            else:
                current_node = current_node.child[1]
    return current_node.y

import numpy as np
import math
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> list[int]:
    """Tiny Isolation Forest implementation.

    Args:
        data: 2-D NumPy array where each row is an observation and each column a feature.
        n_trees: Number of random isolation trees that will be grown.
        sample_size: Number of samples drawn (without replacement) to grow every tree.
        epsilon: Fraction (0 ≤ ε < 1) of observations that are considered outliers.

    Returns:
        A list with the zero-based indices of the detected outliers, sorted increasingly.
    """
    np.random.seed(42)

    def c(n):
        if n == 1:
            return 0
        return 2 * math.log(n - 1) + 0.5772156649 - 2 * (n - 1) / n

    def expected_path_length(n):
        if n <= 1:
            return 0
        return c(n) + 2 * (harmonic_number(n - 1) - 1)

    def harmonic_number(n):
        return sum((1 / i for i in range(1, n + 1)))

    def build_tree(subsample):
        if len(subsample) <= 1 or len(subsample) == sample_size:
            return subsample
        feature_idx = np.random.choice(data.shape[1])
        (feature_min, feature_max) = (np.min(subsample[:, feature_idx]), np.max(subsample[:, feature_idx]))
        if feature_min == feature_max:
            return subsample
        threshold = np.random.uniform(feature_min, feature_max)
        left = subsample[subsample[:, feature_idx] < threshold]
        right = subsample[subsample[:, feature_idx] >= threshold]
        return (feature_idx, threshold, build_tree(left), build_tree(right))

    def path_length(x, tree, current_depth=0):
        if isinstance(tree, np.ndarray):
            return current_depth + c(len(tree))
        (feature_idx, threshold, left, right) = tree
        if x[feature_idx] < threshold:
            return path_length(x, left, current_depth + 1)
        else:
            return path_length(x, right, current_depth + 1)
    trees = []
    for _ in range(n_trees):
        if sample_size >= data.shape[0]:
            subsample = data
        else:
            subsample_indices = np.random.choice(data.shape[0], sample_size, replace=False)
            subsample = data[subsample_indices]
        trees.append(build_tree(subsample))
    path_lengths = np.zeros(data.shape[0])
    for (i, x) in enumerate(data):
        path_lengths[i] = np.mean([path_length(x, tree) for tree in trees])
    n = data.shape[0]
    phi = 2 * math.log(n - 1) - 2 * (n - 1) / n
    anomaly_scores = 2 ** (-path_lengths / phi)
    threshold = np.percentile(anomaly_scores, (1 - epsilon) * 100)
    outliers = np.where(anomaly_scores >= threshold)[0]
    return sorted(outliers.tolist())

import numpy as np
def local_outlier_factor(data, k, epsilon=1.5):
    """Compute the Local Outlier Factor (LOF) for each sample in the dataset."""
    n_samples = len(data)
    distance_matrix = np.zeros((n_samples, n_samples))
    for i in range(n_samples):
        for j in range(i + 1, n_samples):
            distance = np.sqrt(np.sum((data[i] - data[j]) ** 2))
            distance_matrix[i, j] = distance
            distance_matrix[j, i] = distance
    k_distances = np.zeros(n_samples)
    k_neighbors = [None] * n_samples
    for i in range(n_samples):
        distances = distance_matrix[i]
        sorted_indices = np.argsort(distances)
        k_distances[i] = distances[sorted_indices[k]]
        k_neighbors[i] = sorted_indices[1:k + 1]

    def reachability_distance(p, q):
        return max(k_distances[q], distance_matrix[p, q])
    lrd = np.zeros(n_samples)
    for i in range(n_samples):
        sum_reach_dist = 0
        for neighbor in k_neighbors[i]:
            sum_reach_dist += reachability_distance(i, neighbor)
        lrd[i] = k / sum_reach_dist
    lof_scores = np.zeros(n_samples)
    for i in range(n_samples):
        sum_lrd_neighbors = 0
        for neighbor in k_neighbors[i]:
            sum_lrd_neighbors += lrd[neighbor]
        lof_scores[i] = sum_lrd_neighbors / k / lrd[i]
    lof_scores = np.round(lof_scores, 4)
    outlier_indices = np.where(lof_scores > epsilon)[0]
    outlier_indices = sorted(outlier_indices)
    return (list(lof_scores), outlier_indices)
