def isolation_tree_path_length(data: np.ndarray) -> list[int]:
    """Compute the depth of the leaf reached by every sample in a random isolation tree.

    The tree is built following the rules described in the task statement and the
    random seed must be set to 0 so that results are reproducible.

    Args:
        data: A 2-D NumPy array of shape (n_samples, n_features).

    Returns:
        A list of length *n_samples* where the *i-th* element is the number of
        edges from the root to the leaf that contains the *i-th* sample.
    """
    np.random.seed(0)

    def build_tree(indices):
        if len(indices) < 3:
            return {'leaf': indices}
        feature = np.random.choice(data.shape[1])
        down = np.min(data[indices, feature])
        up = np.max(data[indices, feature])
        v = np.random.uniform(down, up)
        left_indices = indices[data[indices, feature] <= v]
        right_indices = indices[data[indices, feature] > v]
        return {'feature': feature, 'value': v, 'left': build_tree(left_indices), 'right': build_tree(right_indices)}

    def traverse_tree(node, index, depth):
        if 'leaf' in node:
            return depth
        feature = node['feature']
        value = node['value']
        if data[index, feature] <= value:
            return traverse_tree(node['left'], index, depth + 1)
        else:
            return traverse_tree(node['right'], index, depth + 1)
    root = build_tree(np.arange(data.shape[0]))
    depths = [traverse_tree(root, i, 0) for i in range(data.shape[0])]
    return depths
