def softplus(x, deriv: bool=False):
    """Compute the SoftPlus activation or its gradient.

    Args:
        x (scalar, list, or np.ndarray): Input values.
        deriv (bool): If True, compute the gradient of SoftPlus.

    Returns:
        list or float: SoftPlus values or their gradients rounded to 4 decimal places.
    """

    def softplus_func(val):
        if val > 20:
            return val
        elif val < -20:
            return np.exp(val)
        else:
            return np.log1p(np.exp(val))

    def sigmoid_func(val):
        return 1 / (1 + np.exp(-val))
    is_scalar = np.isscalar(x)
    x = np.array(x) if not is_scalar else np.array([x])
    if deriv:
        result = sigmoid_func(x)
    else:
        result = np.array([softplus_func(val) for val in x])
    rounded_result = np.round(result, 4)
    return rounded_result.item() if is_scalar else rounded_result.tolist()

