def adaboost_predict(X_train: list[list[int | float]], y_train: list[int], X_test: list[list[int | float]], n_estimators: int=10) -> list[int]:
    """Fill in here. The final implementation must follow the specification given in the task
    description and return a list with the predicted class labels for *X_test*."""

    def predict_stump(X, feature_index, threshold, polarity):
        predictions = np.ones(len(X))
        if polarity == 1:
            predictions[X[:, feature_index] >= threshold] = -1
        else:
            predictions[X[:, feature_index] < threshold] = -1
        return predictions

    def find_best_stump(X, y, weights):
        (m, n) = X.shape
        min_error = float('inf')
        best_stump = {}
        best_predictions = np.zeros(m)
        for feature_index in range(n):
            thresholds = np.unique(X[:, feature_index])
            for threshold in thresholds:
                for polarity in [1, -1]:
                    predictions = predict_stump(X, feature_index, threshold, polarity)
                    weighted_error = np.sum(weights[y != predictions])
                    if weighted_error < min_error:
                        min_error = weighted_error
                        best_stump = {'feature_index': feature_index, 'threshold': threshold, 'polarity': polarity}
                        best_predictions = predictions.copy()
        return (best_stump, best_predictions, min_error)
    X_train = np.array(X_train)
    y_train = np.array(y_train) * 2 - 1
    n_samples = X_train.shape[0]
    weights = np.ones(n_samples) / n_samples
    stumps = []
    for _ in range(n_estimators):
        (best_stump, best_predictions, error) = find_best_stump(X_train, y_train, weights)
        if error == 0:
            break
        alpha = 0.5 * np.log((1 - error) / (error + 1e-10))
        weights *= np.exp(-alpha * y_train * best_predictions)
        weights /= np.sum(weights)
        stumps.append((alpha, best_stump))

    def predict(X_test, stumps):
        X_test = np.array(X_test)
        stump_predictions = np.zeros(len(X_test))
        for (alpha, stump) in stumps:
            (feature_index, threshold, polarity) = (stump['feature_index'], stump['threshold'], stump['polarity'])
            stump_predictions += alpha * predict_stump(X_test, feature_index, threshold, polarity)
        return [1 if pred >= 0 else 0 for pred in stump_predictions]
    return predict(X_test, stumps)

import numpy as np
def decision_tree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, max_depth: int | None=None) -> list[int]:
    """Build a CART decision tree on (X_train, y_train) and predict labels for X_test.

    Parameters
    ----------
    X_train : np.ndarray
        Training feature matrix of shape (n_samples, n_features).
    y_train : np.ndarray
        Integer class labels for the training data, shape (n_samples,).
    X_test : np.ndarray
        Feature matrix to classify, shape (m_samples, n_features).
    max_depth : int | None, optional
        Maximum allowed depth of the tree. If None the depth is unlimited.

    Returns
    -------
    list[int]
        Predicted class label for each row in X_test.
    """

    def gini_impurity(y):
        m = len(y)
        if m == 0:
            return 0.0
        counts = np.bincount(y)
        probabilities = counts / m
        return 1.0 - np.sum(probabilities ** 2)

    def split_data(X, y, feature, threshold):
        left_mask = X[:, feature] <= threshold
        (X_left, y_left) = (X[left_mask], y[left_mask])
        (X_right, y_right) = (X[~left_mask], y[~left_mask])
        return (X_left, y_left, X_right, y_right)

    def best_split(X, y):
        (m, n) = X.shape
        if m == 0:
            return (None, None, None, None, None)
        best_gini = gini_impurity(y)
        best_feature = None
        best_threshold = None
        best_left = None
        best_right = None
        for feature in range(n):
            sorted_indices = np.argsort(X[:, feature])
            sorted_X = X[sorted_indices]
            sorted_y = y[sorted_indices]
            for i in range(1, m):
                if sorted_X[i, feature] != sorted_X[i - 1, feature]:
                    threshold = (sorted_X[i, feature] + sorted_X[i - 1, feature]) / 2.0
                    (X_left, y_left, X_right, y_right) = split_data(X, y, feature, threshold)
                    gini_left = gini_impurity(y_left)
                    gini_right = gini_impurity(y_right)
                    weighted_gini = len(y_left) / m * gini_left + len(y_right) / m * gini_right
                    if weighted_gini < best_gini:
                        best_gini = weighted_gini
                        best_feature = feature
                        best_threshold = threshold
                        best_left = (X_left, y_left)
                        best_right = (X_right, y_right)
        return (best_feature, best_threshold, best_left, best_right, best_gini)

    def build_tree(X, y, depth=0):
        if max_depth is not None and depth >= max_depth:
            return np.bincount(y).argmax()
        if len(np.unique(y)) == 1:
            return y[0]
        (feature, threshold, left, right, gini) = best_split(X, y)
        if left is None or right is None:
            return np.bincount(y).argmax()
        (X_left, y_left) = left
        (X_right, y_right) = right
        if len(y_left) == 0 or len(y_right) == 0:
            return np.bincount(y).argmax()
        left_subtree = build_tree(X_left, y_left, depth + 1)
        right_subtree = build_tree(X_right, y_right, depth + 1)
        return (feature, threshold, left_subtree, right_subtree)

    def predict_tree(node, x):
        if isinstance(node, int):
            return node
        (feature, threshold, left_subtree, right_subtree) = node
        if x[feature] <= threshold:
            return predict_tree(left_subtree, x)
        else:
            return predict_tree(right_subtree, x)
    tree = build_tree(X_train, y_train)
    predictions = [predict_tree(tree, x) for x in X_test]
    return predictions

import numpy as np
def metric(name: str):
    """Factory producing a validated distance function.

    The function creates and returns a callable *d* that computes one of four
    classical distances (Euclidean, Manhattan, Chebyshev, Cosine) between two
    numeric vectors.  All numeric outputs are rounded to four decimal places.

    Validation rules inside the returned callable:
    * Both arguments must be one-dimensional, non-empty, equal-length numeric
      iterables (list, tuple or NumPy array).
    * Metric *name* must be one of the supported strings.
    * For the cosine distance, zero-norm vectors are rejected.

    If the metric name is unsupported or validation fails, *d* returns -1.

    Args:
        name (str): Name of the desired metric.

    Returns:
        Callable[[Iterable, Iterable], float | int]: A distance function with
        integrated validation.
    """

    def validate_vectors(x, y):
        try:
            x = np.array(x, dtype=float)
            y = np.array(y, dtype=float)
        except (TypeError, ValueError):
            return False
        if x.ndim != 1 or y.ndim != 1 or len(x) == 0 or (len(y) == 0) or (len(x) != len(y)):
            return False
        return (x, y)

    def euclidean(x, y):
        return np.sqrt(np.sum((x - y) ** 2))

    def manhattan(x, y):
        return np.sum(np.abs(x - y))

    def chebyshev(x, y):
        return np.max(np.abs(x - y))

    def cosine(x, y):
        norm_x = np.linalg.norm(x)
        norm_y = np.linalg.norm(y)
        if norm_x == 0 or norm_y == 0:
            return -1
        return 1 - np.dot(x, y) / (norm_x * norm_y)
    metrics = {'euclidean': euclidean, 'manhattan': manhattan, 'chebyshev': chebyshev, 'cosine': cosine}
    if name not in metrics:
        return lambda x, y: -1

    def d(x, y):
        validated = validate_vectors(x, y)
        if validated is False:
            return -1
        (x, y) = validated
        result = metrics[name](x, y)
        if result == -1:
            return -1
        return round(result, 4)
    return d

def blackjack_outcome(player: list[int], dealer: list[int]) -> float:
    """Evaluate the outcome of a finished round of Blackjack.

    The function **must** follow the rules presented in the task description.

    Args:
        player: List of integers (1–10) representing the player's final hand. 1 is Ace.
        dealer: List of integers (1–10) representing the dealer's final hand.

    Returns:
        The player's reward as a float. Possible values are -1, 0, 1 or 1.5.
    """

    def hand_value(hand: list[int]) -> int:
        """Calculate the value of a hand, considering the usable ace."""
        value = sum(hand)
        aces = hand.count(1)
        while value > 21 and aces > 0:
            value -= 10
            aces -= 1
        return value

    def is_natural_blackjack(hand: list[int]) -> bool:
        """Check if a hand is a natural blackjack."""
        return len(hand) == 2 and 1 in hand and (10 in hand)
    player_value = hand_value(player)
    dealer_value = hand_value(dealer)
    if player_value > 21:
        return -1.0
    if dealer_value > 21:
        return 1.0
    if is_natural_blackjack(player):
        return 1.5
    if player_value > dealer_value:
        return 1.0
    elif player_value < dealer_value:
        return -1.0
    else:
        return 0.0

