def is_continuous(env: Env, tuple_action: bool, tuple_obs: bool) -> Tuple[bool, bool]:
    """Determine whether the given environment's spaces are continuous.

    A space is *continuous* if it is an instance of `Box`. For composite spaces
    (`Tuple` or `Dict`) the space is continuous only if **all** its sub-spaces
    are `Box`.

    Args:
        env:          Environment exposing `action_space` and `observation_space`.
        tuple_action: Whether the *action* space is composite.
        tuple_obs:    Whether the *observation* space is composite.

    Returns:
        A tuple `(cont_action, cont_obs)` of booleans.
    """

    def is_box(space: Space) -> bool:
        return isinstance(space, Box)

    def all_boxes(spaces: Iterable[Space]) -> bool:
        return all((is_box(space) for space in spaces))

    def all_boxes_in_dict(spaces: Dict[str, Space]) -> bool:
        return all((is_box(space) for space in spaces.values()))
    cont_action = False
    cont_obs = False
    if tuple_action:
        if isinstance(env.action_space, Tuple):
            cont_action = all_boxes(env.action_space.spaces)
        elif isinstance(env.action_space, Dict):
            cont_action = all_boxes_in_dict(env.action_space.spaces)
    else:
        cont_action = is_box(env.action_space)
    if tuple_obs:
        if isinstance(env.observation_space, Tuple):
            cont_obs = all_boxes(env.observation_space.spaces)
        elif isinstance(env.observation_space, Dict):
            cont_obs = all_boxes_in_dict(env.observation_space.spaces)
    else:
        cont_obs = is_box(env.observation_space)
    return (cont_action, cont_obs)

import numpy as np
def gbdt_regression(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, n_estimators: int=10, learning_rate: float=0.1, max_depth: int=3) -> list[float]:
    """Gradient Boosting Decision Tree (GBDT) regressor.

    Parameters
    ----------
    X_train : np.ndarray
        2-D array with shape (n_samples, n_features) containing the training
        features.
    y_train : np.ndarray
        1-D array of length n_samples containing the training targets.
    X_test : np.ndarray
        2-D array with shape (m_samples, n_features) containing the test
        features to predict.
    n_estimators : int, default=10
        Number of boosting iterations.
    learning_rate : float, default=0.1
        Shrinkage factor applied to each tree’s prediction.
    max_depth : int, default=3
        Maximum depth of every individual regression tree.

    Returns
    -------
    list[float]
        Predictions for every sample in *X_test*, rounded to 4 decimal places.
    """

    def mean_squared_error(y_true, y_pred):
        return np.mean((y_true - y_pred) ** 2)

    def best_split(X, y):
        best_score = np.inf
        (best_feature, best_threshold) = (None, None)
        for feature_idx in range(X.shape[1]):
            thresholds = np.unique(X[:, feature_idx])
            for threshold in thresholds:
                left_mask = X[:, feature_idx] < threshold
                right_mask = ~left_mask
                (left_y, right_y) = (y[left_mask], y[right_mask])
                if len(left_y) == 0 or len(right_y) == 0:
                    continue
                score = len(left_y) * mean_squared_error(left_y, np.mean(left_y)) + len(right_y) * mean_squared_error(right_y, np.mean(right_y))
                if score < best_score:
                    best_score = score
                    best_feature = feature_idx
                    best_threshold = threshold
        return (best_feature, best_threshold)

    def build_tree(X, y, depth=0):
        if depth >= max_depth or len(np.unique(y)) == 1:
            return np.mean(y)
        (feature, threshold) = best_split(X, y)
        if feature is None:
            return np.mean(y)
        left_mask = X[:, feature] < threshold
        right_mask = ~left_mask
        left_tree = build_tree(X[left_mask], y[left_mask], depth + 1)
        right_tree = build_tree(X[right_mask], y[right_mask], depth + 1)
        return (feature, threshold, left_tree, right_tree)

    def predict_tree(tree, X):
        if isinstance(tree, float):
            return tree
        (feature, threshold, left_tree, right_tree) = tree
        if X[feature] < threshold:
            return predict_tree(left_tree, X)
        else:
            return predict_tree(right_tree, X)

    def predict_trees(trees, X):
        return np.array([predict_tree(tree, x) for tree in trees])
    predictions = np.full(y_train.shape, np.mean(y_train))
    trees = []
    for _ in range(n_estimators):
        residuals = y_train - predictions
        tree = build_tree(X_train, residuals)
        trees.append(tree)
        predictions += learning_rate * predict_trees([tree], X_train)
    final_predictions = np.full(X_test.shape[0], np.mean(y_train))
    for tree in trees:
        final_predictions += learning_rate * predict_trees([tree], X_test)
    return [round(pred, 4) for pred in final_predictions]

import numpy as np
def root_mean_squared_log_error(actual: list[float], predicted: list[float]) -> float:
    """Calculate the Root Mean Squared Logarithmic Error (RMSLE) between actual and predicted values."""
    if len(actual) != len(predicted):
        return -1
    if any((a < 0 for a in actual)) or any((p < 0 for p in predicted)):
        return -1
    n = len(actual)
    log_diffs = [(np.log1p(pred) - np.log1p(act)) ** 2 for (pred, act) in zip(predicted, actual)]
    rmsle = np.sqrt(1 / n * sum(log_diffs))
    return round(rmsle, 4)

import numpy as np
def sigmoid(x, order: int=0):
    """Compute the logistic sigmoid or its derivatives.

    Args:
        x (float | int | list | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        order (int, optional): 0 = function value, 1 = first derivative,
            2 = second derivative. Defaults to 0.

    Returns:
        float | list: Result rounded to 4 decimals. Scalar input returns a float;
            vector/matrix input returns a Python list preserving the shape.
            If *order* is not 0, 1, or 2, the function returns -1.
    """
    is_scalar = isinstance(x, (int, float))
    x = np.array(x, dtype=float)
    sigma_x = 1 / (1 + np.exp(-x))
    if order == 0:
        result = sigma_x
    elif order == 1:
        result = sigma_x * (1 - sigma_x)
    elif order == 2:
        result = sigma_x * (1 - sigma_x) * (1 - 2 * sigma_x)
    else:
        return -1
    result = np.round(result, 4)
    if is_scalar:
        return float(result)
    else:
        return result.tolist()

import numpy as np
def regression_tree_predict(X_train: list[float | int], y_train: list[float | int], X_test: list[float | int], max_depth: int=3) -> list[float]:
    """Fit a univariate regression tree of depth *max_depth* and predict.

    Parameters
    ----------
    X_train : list[float | int]
        1-D feature values for the training set.
    y_train : list[float | int]
        Continuous target values for the training set.
    X_test : list[float | int]
        1-D feature values for which predictions are required.
    max_depth : int, default 3
        Maximum depth of the binary tree (root has depth 0).

    Returns
    -------
    list[float]
        Predictions for every element in *X_test*.
    """

    def sse(y):
        mean = np.mean(y)
        return np.sum((y - mean) ** 2)

    def best_split(X, y):
        best_sse = float('inf')
        best_split_point = None
        for i in range(1, len(X)):
            left_y = y[:i]
            right_y = y[i:]
            current_sse = sse(left_y) + sse(right_y)
            if current_sse < best_sse:
                best_sse = current_sse
                best_split_point = X[i]
        return best_split_point

    def build_tree(X, y, depth):
        if depth == max_depth or len(X) < 2:
            return np.mean(y)
        split_point = best_split(X, y)
        if split_point is None:
            return np.mean(y)
        left_indices = np.where(np.array(X) < split_point)[0]
        right_indices = np.where(np.array(X) >= split_point)[0]
        left_tree = build_tree(np.array(X)[left_indices], np.array(y)[left_indices], depth + 1)
        right_tree = build_tree(np.array(X)[right_indices], np.array(y)[right_indices], depth + 1)
        return (split_point, left_tree, right_tree)

    def predict_tree(tree, x):
        if isinstance(tree, float):
            return tree
        (split_point, left_tree, right_tree) = tree
        if x < split_point:
            return predict_tree(left_tree, x)
        else:
            return predict_tree(right_tree, x)
    if not X_train or not y_train:
        return []
    (X_train, y_train) = zip(*sorted(zip(X_train, y_train)))
    tree = build_tree(X_train, y_train, 0)
    return [predict_tree(tree, x) for x in X_test]

from collections import Counter, defaultdict
import itertools
def fp_growth(transactions, min_sup):
    """Mine all frequent item-sets using the FP-Growth algorithm.

    Parameters
    ----------
    transactions : Iterable[Iterable[Hashable]]
        A collection of transactions.  Each transaction is an iterable containing hashable items.
    min_sup : int
        Minimum number of occurrences an item-set must have to be considered frequent.

    Returns
    -------
    list[tuple]
        All frequent item-sets sorted 1) by length, 2) lexicographically.  Every
        tuple itself is sorted lexicographically.
    """

    class FPNode:

        def __init__(self, item=None, count=0, parent=None):
            self.item = item
            self.count = count
            self.parent = parent
            self.children = defaultdict(FPNode)
            self.link = None

    def build_header_table(frequent_items):
        header_table = defaultdict(lambda : [0, None])
        for (item, count) in frequent_items.items():
            header_table[item] = [count, None]
        return header_table

    def insert_transaction(transaction, tree, header_table):
        current_node = tree
        for item in transaction:
            if item in current_node.children:
                current_node.children[item].count += 1
            else:
                new_node = FPNode(item, 1, current_node)
                current_node.children[item] = new_node
                if header_table[item][1] is None:
                    header_table[item][1] = new_node
                else:
                    current_node = header_table[item][1]
                    while current_node.link is not None:
                        current_node = current_node.link
                    current_node.link = new_node
            current_node = current_node.children[item]

    def mine_tree(tree, header_table, prefix, frequent_item_sets):
        if is_single_path(tree):
            path_items = get_path_items(tree)
            for i in range(1, len(path_items)):
                for combination in itertools.combinations(path_items, i):
                    frequent_item_sets.add(tuple(sorted(prefix + combination)))
        else:
            for item in get_sorted_items(header_table):
                conditional_pattern_base = find_conditional_pattern_base(item, header_table)
                conditional_tree = build_conditional_tree(conditional_pattern_base, header_table)
                if conditional_tree.children:
                    new_prefix = prefix + (item,)
                    mine_tree(conditional_tree, header_table, new_prefix, frequent_item_sets)

    def is_single_path(node):
        while node.children:
            node = node.children[next(iter(node.children))]
            if len(node.children) > 1:
                return False
        return True

    def get_path_items(node):
        path_items = []
        while node.parent:
            path_items.append(node.item)
            node = node.parent
        return path_items

    def get_sorted_items(header_table):
        return [item for (item, count) in sorted(header_table.items(), key=lambda x: (x[1][0], x[0]), reverse=True)]

    def find_conditional_pattern_base(item, header_table):
        conditional_pattern_base = []
        node = header_table[item][1]
        while node:
            prefix_path = []
            prefix_node = node.parent
            while prefix_node.parent:
                prefix_path.append(prefix_node.item)
                prefix_node = prefix_node.parent
            if prefix_path:
                conditional_pattern_base.append((tuple(prefix_path), node.count))
            node = node.link
        return conditional_pattern_base

    def build_conditional_tree(conditional_pattern_base, header_table):
        conditional_tree = FPNode()
        for (path, count) in conditional_pattern_base:
            insert_transaction(reversed(path), conditional_tree, header_table)
        return conditional_tree
    item_counts = Counter((item for transaction in transactions for item in transaction))
    frequent_items = {item: count for (item, count) in item_counts.items() if count >= min_sup}
    sorted_items = sorted(frequent_items.items(), key=lambda x: (-x[1], x[0]))
    item_order = {item: i for (i, (item, count)) in enumerate(sorted_items)}
    sorted_transactions = [sorted(transaction, key=lambda x: item_order[x], reverse=True) for transaction in transactions]
    fp_tree = FPNode()
    header_table = build_header_table(frequent_items)
    for transaction in sorted_transactions:
        insert_transaction(transaction, fp_tree, header_table)
    frequent_item_sets = set()
    mine_tree(fp_tree, header_table, (), frequent_item_sets)
    return sorted(frequent_item_sets, key=lambda x: (len(x), x))
