import numpy as np
def sigmoid(z):
    """Compute the numerically stable, element-wise sigmoid activation.

    The function must work for a scalar, list/tuple, or NumPy ndarray input.

    Args:
        z: int, float, list, tuple, or numpy.ndarray – input values.

    Returns:
        float if *z* is a scalar, otherwise a Python list obtained with
        ``numpy.ndarray.tolist()``, where every element is the sigmoid of the
        corresponding input value and is rounded to four decimal places.
    """
    if isinstance(z, (int, float)):
        z = np.array([z])
    else:
        z = np.array(z)
    sigmoid_values = np.where(z > 0, 1 / (1 + np.exp(-z)), np.exp(z) / (1 + np.exp(z)))
    sigmoid_values = np.round(sigmoid_values, 4)
    if isinstance(z, (int, float)):
        return float(sigmoid_values[0])
    else:
        return sigmoid_values.tolist()

import numpy as np
def sigmoid_grad(z):
    """Return the derivative of the sigmoid function given its output.

    The input *z* already contains sigmoid values (numbers in [0,1]).  The
    derivative is computed as z*(1-z) element-wise.

    Args:
        z: float, list or NumPy ndarray of sigmoid outputs.

    Returns:
        Same structure as *z* (float or nested list) with each value replaced
        by its gradient, rounded to 4 decimal places.
    """

    def compute_gradient(value):
        return np.round(value * (1 - value), 4)
    if isinstance(z, (int, float)):
        return compute_gradient(z)
    elif isinstance(z, list):
        if not z:
            return []
        return [sigmoid_grad(item) for item in z]
    elif isinstance(z, np.ndarray):
        return np.vectorize(compute_gradient)(z).tolist()
    else:
        raise ValueError('Input must be a float, list, or NumPy ndarray.')

def predict_sample(root: 'Node', features: list | tuple, task_type: str):
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    current_node = root
    while current_node.y is None:
        if task_type == 'classification':
            feature_value = features[current_node.label]
            found_child = False
            for child in current_node.child:
                if child.x == feature_value:
                    current_node = child
                    found_child = True
                    break
            if not found_child:
                current_node = current_node.child[1]
        elif task_type == 'regression':
            feature_value = features[current_node.label]
            if feature_value <= current_node.s:
                current_node = current_node.child[0]
            else:
                current_node = current_node.child[1]
    return current_node.y

import numpy as np
import math
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> list[int]:
    """Tiny Isolation Forest implementation.

    Args:
        data: 2-D NumPy array where each row is an observation and each column a feature.
        n_trees: Number of random isolation trees that will be grown.
        sample_size: Number of samples drawn (without replacement) to grow every tree.
        epsilon: Fraction (0 ≤ ε < 1) of observations that are considered outliers.

    Returns:
        A list with the zero-based indices of the detected outliers, sorted increasingly.
    """
    np.random.seed(42)

    def c(n):
        if n == 1:
            return 0
        return 2 * np.log(n - 1) + 0.5772156649 - 2 * (n - 1) / n

    def expected_path_length(n):
        if n <= 1:
            return 0
        return c(n) + 2 * (np.log(n - 1) - (n - 1) / n)

    def grow_tree(data, height_limit):
        if len(data) <= 1 or height_limit == 0:
            return {'size': len(data)}
        feature_index = np.random.randint(data.shape[1])
        (feature_min, feature_max) = (data[:, feature_index].min(), data[:, feature_index].max())
        if feature_min == feature_max:
            return {'size': len(data)}
        threshold = np.random.uniform(feature_min, feature_max)
        left_indices = data[:, feature_index] < threshold
        right_indices = data[:, feature_index] >= threshold
        left_tree = grow_tree(data[left_indices], height_limit - 1)
        right_tree = grow_tree(data[right_indices], height_limit - 1)
        return {'feature_index': feature_index, 'threshold': threshold, 'left': left_tree, 'right': right_tree}

    def path_length(x, tree, current_height):
        if 'size' in tree:
            return current_height + c(tree['size'])
        feature_index = tree['feature_index']
        threshold = tree['threshold']
        if x[feature_index] < threshold:
            return path_length(x, tree['left'], current_height + 1)
        else:
            return path_length(x, tree['right'], current_height + 1)
    height_limit = math.ceil(np.log2(sample_size))
    trees = [grow_tree(data[np.random.choice(data.shape[0], min(sample_size, data.shape[0]), replace=False)], height_limit) for _ in range(n_trees)]
    path_lengths = np.zeros(data.shape[0])
    for (i, x) in enumerate(data):
        path_lengths[i] = np.mean([path_length(x, tree, 0) for tree in trees])
    expected_avg_path_length = expected_path_length(min(sample_size, data.shape[0]))
    anomaly_scores = 2 ** (-path_lengths / expected_avg_path_length)
    threshold = np.percentile(anomaly_scores, 100 * (1 - epsilon))
    outliers = np.where(anomaly_scores >= threshold)[0]
    return sorted(outliers.tolist())

import math
from collections import defaultdict
import numpy as np
def maxent_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, epsilon: float=0.001, n_iter: int=100) -> list[int]:
    (num_samples, num_features) = X_train.shape
    labels = set(y_train)
    feature_count = defaultdict(int)
    empirical_joint = defaultdict(int)
    empirical_marginal_y = defaultdict(int)
    for (x, y) in zip(X_train, y_train):
        empirical_marginal_y[y] += 1
        for (feature_index, feature_value) in enumerate(x):
            feature_count[feature_index, feature_value] += 1
            empirical_joint[feature_index, feature_value, y] += 1
    empirical_marginal_y = {y: count / num_samples for (y, count) in empirical_marginal_y.items()}
    empirical_joint = {k: v / num_samples for (k, v) in empirical_joint.items()}
    weights = defaultdict(float)
    expected_counts = defaultdict(float)
    for iteration in range(n_iter):
        expected_counts.clear()
        for y in labels:
            for feature_index in range(num_features):
                for feature_value in set(X_train[:, feature_index]):
                    z_x = sum((math.exp(sum((weights[j, fv, y] for (j, fv) in enumerate(x)))) for x in X_train if x[feature_index] == feature_value))
                    expected_counts[feature_index, feature_value, y] = sum((math.exp(sum((weights[j, fv, y] for (j, fv) in enumerate(x)))) / z_x for (x, y_train) in zip(X_train, y_train) if x[feature_index] == feature_value and y_train == y))
        max_update = 0
        for (feature_index, feature_value, y) in expected_counts:
            empirical_count = empirical_joint[feature_index, feature_value, y] * num_samples
            update = math.log(empirical_count / expected_counts[feature_index, feature_value, y])
            weights[feature_index, feature_value, y] += update
            max_update = max(max_update, abs(update))
        if max_update < epsilon:
            break
    predictions = []
    for x in X_test:
        max_prob = -float('inf')
        predicted_label = None
        for y in labels:
            score = 0
            for (feature_index, feature_value) in enumerate(x):
                score += weights[feature_index, feature_value, y]
            prob = math.exp(score) / sum((math.exp(sum((weights[j, fv, y] for (j, fv) in enumerate(x)))) for x in X_train))
            if prob > max_prob:
                max_prob = prob
                predicted_label = y
        predictions.append(predicted_label)
    return predictions

import numpy as np
def local_outlier_factor(data, k, epsilon=1.5):
    """Implement the Local Outlier Factor (LOF) algorithm from scratch."""
    n = len(data)
    distance_matrix = np.zeros((n, n))
    for i in range(n):
        for j in range(i + 1, n):
            distance = np.sqrt(np.sum((data[i] - data[j]) ** 2))
            distance_matrix[i, j] = distance
            distance_matrix[j, i] = distance
    k_distances = np.zeros(n)
    k_neighbors = [None] * n
    for i in range(n):
        distances = distance_matrix[i]
        sorted_indices = np.argsort(distances)[1:k + 1]
        k_distances[i] = distances[sorted_indices[-1]]
        k_neighbors[i] = sorted_indices

    def reachability_distance(p, q):
        return max(k_distances[q], distance_matrix[p, q])
    lrd = np.zeros(n)
    for i in range(n):
        sum_reach_dist = 0
        for neighbor in k_neighbors[i]:
            sum_reach_dist += reachability_distance(i, neighbor)
        lrd[i] = k / sum_reach_dist if sum_reach_dist != 0 else 0
    lof_scores = np.zeros(n)
    for i in range(n):
        sum_lrd_neighbors = 0
        for neighbor in k_neighbors[i]:
            sum_lrd_neighbors += lrd[neighbor]
        lof_scores[i] = sum_lrd_neighbors / k / lrd[i] if lrd[i] != 0 else 0
    lof_scores_rounded = np.round(lof_scores, 4).tolist()
    outlier_indices = [i for (i, score) in enumerate(lof_scores) if score > epsilon]
    outlier_indices.sort()
    return (lof_scores_rounded, outlier_indices)
