def apriori_frequent_itemsets(transactions: list[list[int]], min_sup: float) -> list[tuple]:
    """Find frequent itemsets with the Apriori algorithm.

    Parameters
    ----------
    transactions : list[list[Hashable]]
        List of transactions; each transaction is itself a list of items.
    min_sup : float
        Minimum support threshold expressed as a fraction (>0 and ≤1).

    Returns
    -------
    list[tuple]
        Frequent itemsets ordered by length and then lexicographically.
    """

    def support(itemset):
        return sum((1 for transaction in transactions if set(itemset).issubset(transaction))) / len(transactions)

    def generate_candidates(itemsets, k):
        candidates = set()
        for a in itemsets:
            for b in itemsets:
                c = tuple(sorted(set(a).union(set(b))))
                if len(c) == k and all((tuple(sorted(subset)) in itemsets for subset in itertools.combinations(c, k - 1))):
                    candidates.add(c)
        return candidates
    item_counts = {}
    for transaction in transactions:
        for item in transaction:
            item_counts[item] = item_counts.get(item, 0) + 1
    num_transactions = len(transactions)
    frequent_itemsets = {frozenset([item]): count / num_transactions for (item, count) in item_counts.items() if count / num_transactions >= min_sup}
    k = 2
    while frequent_itemsets:
        candidate_itemsets = generate_candidates(frequent_itemsets, k)
        candidate_counts = {itemset: 0 for itemset in candidate_itemsets}
        for transaction in transactions:
            for itemset in candidate_counts:
                if set(itemset).issubset(transaction):
                    candidate_counts[itemset] += 1
        frequent_itemsets = {itemset: count / num_transactions for (itemset, count) in candidate_counts.items() if count / num_transactions >= min_sup}
        if frequent_itemsets:
            result.extend(sorted(frequent_itemsets.keys(), key=lambda x: (len(x), x)))
        k += 1
    result = sorted(result, key=lambda x: (len(x), x))
    return [tuple(itemset) for itemset in result]
