def dbscan(data: np.ndarray, eps: float, min_samples: int) -> list[int]:
    """Cluster *data* with the DBSCAN algorithm.

    Parameters
    ----------
    data : np.ndarray
        Two-dimensional array where each row is a sample and columns are
        features.
    eps : float
        Maximum radius of the neighborhood to be considered reachable.
    min_samples : int
        Minimum number of samples (including the point itself) required to form
        a dense region.

    Returns
    -------
    list[int]
        Cluster labels for each sample. Noise points are labeled ‑1 and cluster
        indices start at 0 and increase sequentially in discovery order.
    """
    if data.size == 0:
        return []

    def euclidean_distance(point1, point2):
        return np.sqrt(np.sum((point1 - point2) ** 2))

    def region_query(point_index):
        neighbors = []
        for (index, point) in enumerate(data):
            if euclidean_distance(data[point_index], point) <= eps:
                neighbors.append(index)
        return neighbors

    def expand_cluster(point_index, cluster_id):
        seeds = region_query(point_index)
        if len(seeds) < min_samples:
            labels[point_index] = -1
            return False
        else:
            labels[point_index] = cluster_id
            i = 0
            while i < len(seeds):
                neighbor_index = seeds[i]
                if labels[neighbor_index] == -1:
                    labels[neighbor_index] = cluster_id
                elif labels[neighbor_index] == 0:
                    labels[neighbor_index] = cluster_id
                    new_neighbors = region_query(neighbor_index)
                    if len(new_neighbors) >= min_samples:
                        seeds.extend(new_neighbors)
                i += 1
            return True
    labels = [0] * len(data)
    cluster_id = 0
    for point_index in range(len(data)):
        if labels[point_index] != 0:
            continue
        if expand_cluster(point_index, cluster_id):
            cluster_id += 1
    return labels

