def regression_tree_predict(X_train: list[float | int], y_train: list[float | int], X_test: list[float | int], max_depth: int=3) -> list[float]:
    """Fit a univariate regression tree of depth *max_depth* and predict.

    Parameters
    ----------
    X_train : list[float | int]
        1-D feature values for the training set.
    y_train : list[float | int]
        Continuous target values for the training set.
    X_test : list[float | int]
        1-D feature values for which predictions are required.
    max_depth : int, default 3
        Maximum depth of the binary tree (root has depth 0).

    Returns
    -------
    list[float]
        Predictions for every element in *X_test*.
    """

    def sse(y):
        return np.sum((y - np.mean(y)) ** 2)

    def best_split(X, y):
        min_sse = float('inf')
        best_split_point = None
        for i in range(1, len(X)):
            left_sse = sse(y[:i])
            right_sse = sse(y[i:])
            current_sse = left_sse + right_sse
            if current_sse < min_sse:
                min_sse = current_sse
                best_split_point = i
        return best_split_point

    def build_tree(X, y, depth):
        if depth == max_depth or len(X) < 2:
            return np.mean(y)
        split_point = best_split(X, y)
        if split_point is None:
            return np.mean(y)
        left_tree = build_tree(X[:split_point], y[:split_point], depth + 1)
        right_tree = build_tree(X[split_point:], y[split_point:], depth + 1)
        return (X[split_point - 1], left_tree, right_tree)

    def predict_tree(tree, x):
        if isinstance(tree, float):
            return tree
        (split_point, left_tree, right_tree) = tree
        if x <= split_point:
            return predict_tree(left_tree, x)
        else:
            return predict_tree(right_tree, x)
    if not X_train or not y_train:
        return []
    sorted_indices = np.argsort(X_train)
    X_train_sorted = np.array(X_train)[sorted_indices]
    y_train_sorted = np.array(y_train)[sorted_indices]
    tree = build_tree(X_train_sorted, y_train_sorted, 0)
    predictions = [predict_tree(tree, x) for x in X_test]
    return predictions

